import numpy as np
def glm_links():
    """Construct and return standard GLM link functions.

    Returns
    -------
    dict
        A three-entry dictionary (identity, log, logit) where each entry is a
        dictionary containing callables for the link, its inverse and its
        derivative with respect to μ. All functions must work with numpy
        scalars as well as 1-D/2-D numpy arrays via element-wise operations.
    """

    def identity_link(mu):
        return mu

    def inv_identity_link(eta):
        return eta

    def d_identity_link(mu):
        return np.ones_like(mu)

    def log_link(mu):
        return np.log(mu)

    def inv_log_link(eta):
        return np.exp(eta)

    def d_log_link(mu):
        return 1 / mu

    def logit_link(mu):
        return np.log(mu / (1 - mu))

    def inv_logit_link(eta):
        return 1 / (1 + np.exp(-eta))

    def d_logit_link(mu):
        return mu * (1 - mu)
    return {'identity': {'link': identity_link, 'inv_link': inv_identity_link, 'd_link': d_identity_link}, 'log': {'link': log_link, 'inv_link': inv_log_link, 'd_link': d_log_link}, 'logit': {'link': logit_link, 'inv_link': inv_logit_link, 'd_link': d_logit_link}}
