def adaboost_1d_predict(x_train: list[float], y_train: list[int], x_test: list[float], epsilon: float=0.0) -> list[int]:
    """Trains a 1-D AdaBoost ensemble and returns predictions."""
    n_samples = len(x_train)
    weights = np.ones(n_samples) / n_samples
    stumps = []
    alphas = []
    while True:
        min_error = float('inf')
        best_stump = None
        sorted_indices = np.argsort(x_train)
        sorted_x = np.array(x_train)[sorted_indices]
        sorted_y = np.array(y_train)[sorted_indices]
        for i in range(n_samples - 1):
            theta = (sorted_x[i] + sorted_x[i + 1]) / 2
            for d in [0, 1]:
                if d == 0:
                    predictions = np.where(sorted_x <= theta, 1, -1)
                else:
                    predictions = np.where(sorted_x > theta, 1, -1)
                errors = np.where(predictions != sorted_y, 1, 0)
                weighted_error = np.sum(errors * weights[sorted_indices])
                if weighted_error < min_error:
                    min_error = weighted_error
                    best_stump = {'theta': theta, 'd': d}
        if min_error == 0:
            alpha = 1.0
        else:
            alpha = 0.5 * math.log((1 - min_error) / min_error)
        alphas.append(alpha)
        stumps.append(best_stump)
        for i in range(n_samples):
            if best_stump['d'] == 0:
                pred = 1 if x_train[i] <= best_stump['theta'] else -1
            else:
                pred = 1 if x_train[i] > best_stump['theta'] else -1
            exponent = -alpha * y_train[i] * pred
            weights[i] *= math.exp(exponent)
        weights /= np.sum(weights)
        training_predictions = np.zeros(n_samples)
        for i in range(len(stumps)):
            for j in range(n_samples):
                if stumps[i]['d'] == 0:
                    pred = 1 if x_train[j] <= stumps[i]['theta'] else -1
                else:
                    pred = 1 if x_train[j] > stumps[i]['theta'] else -1
                training_predictions[j] += alphas[i] * pred
        training_errors = np.where(np.sign(training_predictions) != np.array(y_train), 1, 0)
        training_error_rate = np.mean(training_errors)
        if training_error_rate <= epsilon:
            break
    test_predictions = np.zeros(len(x_test))
    for i in range(len(stumps)):
        for j in range(len(x_test)):
            if stumps[i]['d'] == 0:
                pred = 1 if x_test[j] <= stumps[i]['theta'] else -1
            else:
                pred = 1 if x_test[j] > stumps[i]['theta'] else -1
            test_predictions[j] += alphas[i] * pred
    return [int(math.copysign(1, p)) for p in test_predictions]
