def softplus(x, deriv: bool=False):
    """
    Compute the SoftPlus activation function or its derivative (sigmoid).

    Parameters:
    x: A scalar, list, or NumPy array of real numbers.
    deriv: A boolean indicating whether to compute the SoftPlus function (False)
           or its derivative (True, sigmoid function).

    Returns:
    The computed SoftPlus values or their derivatives, rounded to the nearest 
    4th decimal, as a Python list or a single float if the input is a scalar.
    """
    x = np.array(x)
    if deriv:
        result = 1 / (1 + np.exp(-x))
    else:
        result = np.where(x >= 0, x + np.log1p(np.exp(-x)), np.log1p(np.exp(x)))
    result = np.round(result, decimals=4)
    if isinstance(x, np.ndarray) and len(x.shape) == 0:
        return float(result)
    else:
        return result.tolist()

