import numpy as np
def extract_ngrams(tokens: List[str], N: int):
    """Extracts all N-grams from a list of tokens."""
    return [tuple(tokens[i:i + N]) for i in range(len(tokens) - N + 1)]
def unsmoothed_ngram_log_prob(corpus: List[str], sequence: List[str], N: int) -> float:
    """Compute the unsmoothed MLE N-gram log-probability of *sequence*."""
    corpus_ngrams = extract_ngrams(corpus, N)
    corpus_counts = Counter(corpus_ngrams)
    corpus_prefixes = extract_ngrams(corpus, N - 1)
    corpus_prefix_counts = Counter(corpus_prefixes)
    sequence_ngrams = extract_ngrams(sequence, N)
    log_prob = 0.0
    for ngram in sequence_ngrams:
        count_ngram = corpus_counts[ngram]
        if N == 1:
            total_count = len(corpus)
        else:
            prefix = ngram[:-1]
            total_count = corpus_prefix_counts[prefix]
        if count_ngram == 0 or total_count == 0:
            return float('-inf')
        prob = count_ngram / total_count
        log_prob += np.log(prob)
    return round(log_prob, 4)

