def fp_growth(transactions: List[List[str]], min_support: int) -> List[List[str]]:

    def update_tree(items: List[str], tree: dict, count: int):
        for item in items:
            if item in tree.children:
                tree.children[item].count += count
            else:
                tree.children[item] = FPTreeNode(item, count, tree)
            tree = tree.children[item]

    def mine(tree_root: 'FPTreeNode', header_table: dict, prefix: List[str]):
        frequent_items = []
        for item in header_table.keys():
            new_prefix = prefix + [item]
            frequent_items.append(new_prefix)
            conditional_patterns_base = get_conditional_patterns(header_table[item])
            (conditional_tree_root, conditional_header_table) = build_tree(conditional_patterns_base, min_support)
            if conditional_header_table:
                for itemset in mine(conditional_tree_root, conditional_header_table, new_prefix):
                    frequent_items.append(itemset)
        return frequent_items

    def get_conditional_patterns(path_end_node: 'FPTreeNode'):
        paths = []
        while path_end_node:
            path = []
            condition_path = path_end_node
            while condition_path.parent.item is not None:
                path.append(condition_path.item)
                condition_path = condition_path.parent
            if len(path) > 0:
                paths.append((path, path_end_node.count))
            path_end_node = path_end_node.link
        return paths

    def build_tree(trans: List[List[str]], min_sup: int):
        header_table = defaultdict(int)
        for trans in trans:
            for item in trans:
                header_table[item] += 1
        header_table = {item: count for (item, count) in header_table.items() if count >= min_sup}
        header_table = {item: [] for (item, count) in sorted(header_table.items(), key=lambda x: (-x[1], x[0]))}
        tree_root = FPTreeNode('root', 1)
        for trans in trans:
            trans = [item for item in trans if item in header_table]
            trans.sort(key=lambda item: header_table[item], reverse=True)
            update_tree(trans, tree_root, 1)
        for item in header_table:
            nodes = [node for node in tree_root.find_nodes(item)]
            header_table[item].extend(nodes)
        return (tree_root, header_table)

    class FPTreeNode:

        def __init__(self, item: str, count: int, parent: 'FPTreeNode'):
            self.item = item
            self.count = count
            self.parent = parent
            self.children = {}
            self.link = None

        def find_nodes(self, item: str):
            if self.item == item:
                yield self
            for child in self.children.values():
                yield from child.find_nodes(item)
    if not transactions or min_support < 1:
        return []
    flat_transactions = [item for sublist in transactions for item in sublist]
    item_counts = Counter(flat_transactions)
    filtered_transactions = [[item for item in transaction if item_counts[item] >= min_support] for transaction in transactions]
    (tree_root, header_table) = build_tree(filtered_transactions, min_support)
    frequent_item_sets = mine(tree_root, header_table, [])
    frequent_item_sets.sort(key=lambda x: (len(x), x))
    return frequent_item_sets
