import random
def euclidean_distance(a, b):
    """Calculate the Euclidean distance between two points."""
    return np.linalg.norm(np.array(a) - np.array(b))
def initialize_centroids_kmeanspp(X, K, random_state=None):
    """Initialize centroids using the K-Means++ method."""
    if random_state is not None:
        np.random.seed(random_state)
        random.seed(random_state)
    centroids = [X[random.randint(0, len(X) - 1)]]
    for _ in range(1, K):
        dist = np.array([min([euclidean_distance(x, c) for c in centroids]) for x in X])
        probs = dist / dist.sum()
        cumulative_probs = probs.cumsum()
        r = random.random()
        for (j, p) in enumerate(cumulative_probs):
            if r < p:
                i = j
                break
        centroids.append(X[i])
    return np.array(centroids)
def kmeans(X: np.ndarray, K: int, max_iter: int=100, random_state: int | None=None) -> list[list[float]]:
    """Perform K-Means clustering with K-Means++ initialization."""
    if random_state is not None:
        np.random.seed(random_state)
        random.seed(random_state)
    centroids = initialize_centroids_kmeanspp(X, K, random_state)
    prev_centroids = np.zeros_like(centroids)
    clusters = np.zeros(len(X))
    while True:
        for (i, point) in enumerate(X):
            distances = [euclidean_distance(point, c) for c in centroids]
            cluster = np.argmin(distances)
            clusters[i] = cluster
        new_centroids = []
        for i in range(K):
            points = [X[j] for j in range(len(X)) if clusters[j] == i]
            if points:
                new_centroids.append(np.mean(points, axis=0))
            else:
                new_centroids.append(centroids[i])
        new_centroids = np.array(new_centroids)
        if np.allclose(prev_centroids, new_centroids) or max_iter == 0:
            break
        centroids = new_centroids
        prev_centroids = centroids
        max_iter -= 1
    centroids = np.round(centroids, 4)
    centroids = centroids[centroids[:, 0].argsort()]
    return centroids.tolist()

import numpy as np
def knn_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, k: int, metric: str='euclidean') -> np.ndarray:
    """Implement the k-Nearest Neighbors classifier."""

    def euclidean_distance(x1, x2):
        return np.sqrt(np.sum((x1 - x2) ** 2))

    def manhattan_distance(x1, x2):
        return np.sum(np.abs(x1 - x2))

    def cosine_distance(x1, x2):
        dot_product = np.dot(x1, x2)
        norm_x1 = np.linalg.norm(x1)
        norm_x2 = np.linalg.norm(x2)
        return 1 - dot_product / (norm_x1 * norm_x2)
    distances = []
    if metric == 'euclidean':
        dist_func = euclidean_distance
    elif metric == 'manhattan':
        dist_func = manhattan_distance
    elif metric == 'cosine':
        dist_func = cosine_distance
    else:
        dist_func = euclidean_distance
    for test_sample in X_test:
        dists = np.array([dist_func(test_sample, train_sample) for train_sample in X_train])
        k_nearest_indices = np.argsort(dists)[:k]
        k_nearest_labels = y_train[k_nearest_indices]
        (unique_labels, counts) = np.unique(k_nearest_labels, return_counts=True)
        max_count_index = np.argmax(counts)
        distances.append(unique_labels[max_count_index])
    return np.array(distances)

import numpy as np
def adaboost_predict(X_train: list[list[int | float]], y_train: list[int], X_test: list[list[int | float]], n_estimators: int=10) -> list[int]:

    def create_weak_classifier(X, y, sample_weight):
        best_error = float('inf')
        best_stump = None
        for (i, feature) in enumerate(zip(*X)):
            thresholds = sorted(set(feature))
            for threshold in thresholds:
                for polarity in [-1, 1]:
                    pred = np.array([polarity * (val < threshold) for val in feature])
                    error = sum((w for (x, y_, w) in zip(pred, y, sample_weight) if x != y_))
                    if error < best_error:
                        best_error = error
                        best_stump = {'index': i, 'threshold': threshold, 'polarity': polarity}
                    elif error == best_error:
                        if i < best_stump['index']:
                            best_stump = {'index': i, 'threshold': threshold, 'polarity': polarity}
                        elif i == best_stump['index'] and threshold < best_stump['threshold']:
                            best_stump = {'index': i, 'threshold': threshold, 'polarity': polarity}
                        elif i == best_stump['index'] and threshold == best_stump['threshold'] and (polarity > best_stump['polarity']):
                            best_stump = {'index': i, 'threshold': threshold, 'polarity': polarity}
        return (best_stump, best_error)

    def predict(stump, sample):
        return stump['polarity'] * (sample[stump['index']] < stump['threshold'])
    X_train = np.array(X_train)
    y_train = np.array(y_train)
    y_train[y_train == 0] = -1
    X_test = np.array(X_test)
    n_samples = len(y_train)
    sample_weight = np.full(n_samples, 1 / n_samples)
    estimators = []
    estimator_weights = []
    for _ in range(n_estimators):
        (stump, error) = create_weak_classifier(X_train, y_train, sample_weight)
        if error == 0:
            break
        alpha = 0.5 * np.log((1 - error) / (error + 1e-10))
        estimator_weights.append(alpha)
        estimators.append(stump)
        pred = np.array([predict(stump, sample) for sample in X_train])
        sample_weight *= np.exp(-alpha * y_train * pred)
        sample_weight /= sample_weight.sum()
    final_predictions = np.zeros(len(X_test))
    for (estimator, alpha) in zip(estimators, estimator_weights):
        final_predictions += alpha * np.array([predict(estimator, sample) for sample in X_test])
    return [int(pred >= 0) for pred in final_predictions]

import numpy as np
def gini_impurity(y):
    (_, counts) = np.unique(y, return_counts=True)
    probabilities = counts / len(y)
    return 1 - np.sum(probabilities ** 2)
def best_split(X, y, max_depth=None, current_depth=0):
    if len(np.unique(y)) == 1 or (max_depth is not None and current_depth >= max_depth):
        return {'value': np.argmax(np.bincount(y))}
    best_gini_gain = -np.inf
    best_feature = None
    best_threshold = None
    for feature_index in range(X.shape[1]):
        unique_values = np.unique(X[:, feature_index])
        for i in range(len(unique_values) - 1):
            threshold = (unique_values[i] + unique_values[i + 1]) / 2
            left_indices = X[:, feature_index] <= threshold
            right_indices = X[:, feature_index] > threshold
            gini_left = gini_impurity(y[left_indices])
            gini_right = gini_impurity(y[right_indices])
            gini_gain = gini_impurity(y) - (len(y[left_indices]) / len(y) * gini_left + len(y[right_indices]) / len(y) * gini_right)
            if gini_gain > best_gini_gain:
                best_gini_gain = gini_gain
                best_feature = feature_index
                best_threshold = threshold
    if best_gini_gain <= 0:
        return {'value': np.argmax(np.bincount(y))}
    left_indices = X[:, best_feature] <= best_threshold
    right_indices = X[:, best_feature] > best_threshold
    left = best_split(X[left_indices], y[left_indices], max_depth=max_depth, current_depth=current_depth + 1)
    right = best_split(X[right_indices], y[right_indices], max_depth=max_depth, current_depth=current_depth + 1)
    return {'feature': best_feature, 'threshold': best_threshold, 'left': left, 'right': right}
def predict_tree(example, tree):
    if 'value' in tree:
        return tree['value']
    else:
        feature_value = example[tree['feature']]
        if feature_value <= tree['threshold']:
            return predict_tree(example, tree['left'])
        else:
            return predict_tree(example, tree['right'])
def decision_tree_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, max_depth: int | None=None) -> list[int]:
    tree = best_split(X_train, y_train, max_depth=max_depth)
    predictions = [predict_tree(x, tree) for x in X_test]
    return predictions

import numpy as np
def metric(name: str):
    """Factory producing a validated distance function."""

    def validate(v1, v2):
        if not (isinstance(v1, (list, tuple, np.ndarray)) and isinstance(v2, (list, tuple, np.ndarray))):
            return False
        try:
            v1 = np.array(v1, dtype=float)
            v2 = np.array(v2, dtype=float)
        except ValueError:
            return False
        if v1.ndim != 1 or v2.ndim != 1 or len(v1) == 0 or (len(v2) == 0) or (len(v1) != len(v2)):
            return False
        return True

    def euclidean(v1, v2):
        if not validate(v1, v2):
            return -1
        dist = np.sqrt(np.sum((np.array(v1) - np.array(v2)) ** 2))
        return round(dist, 4)

    def manhattan(v1, v2):
        if not validate(v1, v2):
            return -1
        dist = np.sum(np.abs(np.array(v1) - np.array(v2)))
        return round(dist, 4)

    def chebyshev(v1, v2):
        if not validate(v1, v2):
            return -1
        dist = np.max(np.abs(np.array(v1) - np.array(v2)))
        return round(dist, 4)

    def cosine(v1, v2):
        if not validate(v1, v2):
            return -1
        v1 = np.array(v1)
        v2 = np.array(v2)
        norm_v1 = np.linalg.norm(v1)
        norm_v2 = np.linalg.norm(v2)
        if norm_v1 == 0 or norm_v2 == 0:
            return -1
        dist = 1 - np.dot(v1, v2) / (norm_v1 * norm_v2)
        return round(dist, 4)
    metrics = {'euclidean': euclidean, 'manhattan': manhattan, 'chebyshev': chebyshev, 'cosine': cosine}
    if name not in metrics:
        return lambda v1, v2: -1
    else:
        return metrics[name]

def calculate_score(hand: list[int]) -> int:
    """Calculate the optimal score of a given hand in Blackjack."""
    score = sum(hand)
    aces = hand.count(1)
    while aces > 0 and score + 10 <= 21:
        score += 10
        aces -= 1
    return min(score, 21)
def is_natural_blackjack(hand: list[int]) -> bool:
    """Check if the given hand is a natural blackjack."""
    return len(hand) == 2 and 1 in hand and (10 in hand)
def blackjack_outcome(player: list[int], dealer: list[int]) -> float:
    """Evaluate the outcome of a finished round of Blackjack.

    The function **must** follow the rules presented in the task description.

    Args:
        player: List of integers (1–10) representing the player's final hand. 1 is Ace.
        dealer: List of integers (1–10) representing the dealer's final hand.

    Returns:
        The player's reward as a float. Possible values are -1, 0, 1 or 1.5.
    """
    player_score = calculate_score(player)
    dealer_score = calculate_score(dealer)
    if player_score > 21:
        return -1
    elif dealer_score > 21:
        return 1
    elif player_score > dealer_score:
        if is_natural_blackjack(player):
            return 1.5
        else:
            return 1
    elif player_score < dealer_score:
        return -1
    else:
        return 0

import numpy as np
def sigmoid(z):
    """Compute the element-wise sigmoid (logistic) function.

    The function must work for scalar numbers, Python lists and NumPy arrays and
    must be numerically stable for very large positive or negative inputs.

    Args:
        z: A scalar (int/float) or array-like object (list or np.ndarray)
           containing numeric values.

    Returns:
        float | list: The sigmoid of the input rounded to 4 decimal places.
        If `z` is a scalar the return value is a float, otherwise it is a Python
        list with the same shape as the input.
    """
    if isinstance(z, (int, float)):
        z = np.array([z])
    else:
        try:
            z = np.array(z)
        except Exception as e:
            raise ValueError('Input must be a scalar, list, or np.ndarray') from e

    def stable_sigmoid(z):
        result = np.where(z >= 0, 1 / (1 + np.exp(-z)), np.exp(z) / (1 + np.exp(z)))
        return np.round(result, decimals=4)
    result = stable_sigmoid(z)
    if len(result.shape) == 0:
        return float(result)
    else:
        return result.tolist()

import numpy as np
def leaky_relu(z, a=0.01):
    """Apply the Leaky ReLU activation to every element in *z*.

    Args:
        z: A scalar, list (possibly nested) or ``numpy.ndarray`` of numbers.
        a: Optional float in [0,1) — the slope for negative inputs. Defaults to 0.01.

    Returns:
        A Python list with the same structure as *z* where each value has been transformed
        by the Leaky ReLU activation.
    """

    def apply_leaky_relu(x):
        return max(a * x, x)
    if isinstance(z, (int, float)):
