import numpy as np
def actor_critic_forward(state: list[float], params: dict[str, list]) -> tuple[list[float], float]:
    """Compute a forward pass through a three-layer Actor–Critic network."""

    def relu(x):
        return np.maximum(0, x)

    def softmax(x):
        e_x = np.exp(x - np.max(x))
        return e_x / e_x.sum()
    state = np.array(state)
    for key in params:
        params[key] = np.array(params[key])
    l1_out = relu(np.dot(state, params['W1']) + params['b1'])
    l2_out = relu(np.dot(l1_out, params['W2']) + params['b2'])
    l3_out = relu(np.dot(l2_out, params['W3']) + params['b3'])
    actor_out = np.dot(l3_out, params['Wa']) + params['ba']
    probs = softmax(actor_out)
    value = np.dot(l3_out, params['Wc']) + params['bc']
    probs = [round(p, 4) if p not in (0.0, 1.0) else round(p, 1) for p in probs]
    value = round(value[0], 4) if value[0] not in (0.0, 1.0) else round(value[0], 1)
    return (probs, value)

