def mel_to_hertz(mel):
    return 700 * (10 ** (mel / 2595) - 1)
def hertz_to_mel(hertz):
    return 2595 * np.log10(1 + hertz / 700)
def mel_filterbank(N: int, n_filters: int=20, fs: int=44000, min_freq: int=0, max_freq: int | None=None, normalize: bool=True) -> list[list[float]]:
    """Build a Mel filterbank transformation matrix.

    The returned matrix has *n_filters* rows and *N//2 + 1* columns.  Each row
    is a triangular filter defined on the Mel scale.  See the detailed task
    description for the exact algorithm that has to be implemented.
    """
    if max_freq is None:
        max_freq = fs / 2
    min_mel = hertz_to_mel(min_freq)
    max_mel = hertz_to_mel(max_freq)
    mels = np.linspace(min_mel, max_mel, n_filters + 2)
    freqs = mel_to_hertz(mels)
    bins = np.floor((N + 1) * freqs / fs).astype(int)
    fbank = np.zeros((n_filters, N // 2 + 1))
    for i in range(1, n_filters + 1):
        for k in range(N // 2 + 1):
            freq = k * fs / N
            if freqs[i - 1] <= freq <= freqs[i]:
                fbank[i - 1, k] = (freq - freqs[i - 1]) / (freqs[i] - freqs[i - 1])
            elif freqs[i] < freq <= freqs[i + 1]:
                fbank[i - 1, k] = (freqs[i + 1] - freq) / (freqs[i + 1] - freqs[i])
    if normalize:
        for i in range(n_filters):
            fbank[i] *= 2 / (freqs[i + 2] - freqs[i])
    return [list(map(lambda x: round(x, 4), row)) for row in fbank]

