def find_best_split(X, y):
    best_mse = np.inf
    best_feature = None
    best_threshold = None
    for feature_idx in range(X.shape[1]):
        thresholds = np.unique(X[:, feature_idx])
        for threshold in thresholds:
            left_indices = X[:, feature_idx] < threshold
            right_indices = X[:, feature_idx] >= threshold
            if all(left_indices) or not any(left_indices):
                continue
            mse_left = ((y[left_indices] - np.mean(y[left_indices])) ** 2).mean()
            mse_right = ((y[right_indices] - np.mean(y[right_indices])) ** 2).mean()
            mse = (mse_left * sum(left_indices) + mse_right * sum(right_indices)) / len(y)
            if mse < best_mse:
                best_mse = mse
                best_feature = feature_idx
                best_threshold = threshold
    return (best_feature, best_threshold)
class Node:

    def __init__(self, depth=0):
        self.left = None
        self.right = None
        self.feature = None
        self.threshold = None
        self.value = None
        self.depth = depth

    def fit(self, X, y, max_depth):
        if len(np.unique(y)) == 1 or self.depth >= max_depth:
            self.value = np.mean(y)
            return
        (feature, threshold) = find_best_split(X, y)
        if feature is not None:
            self.feature = feature
            self.threshold = threshold
            left_indices = X[:, feature] < threshold
            right_indices = ~left_indices
            self.left = Node(depth=self.depth + 1)
            self.left.fit(X[left_indices], y[left_indices], max_depth)
            self.right = Node(depth=self.depth + 1)
            self.right.fit(X[right_indices], y[right_indices], max_depth)

    def predict(self, x):
        if self.value is not None:
            return self.value
        elif x[self.feature] < self.threshold:
            return self.left.predict(x)
        else:
            return self.right.predict(x)
def gbdt_regression(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, n_estimators: int=10, learning_rate: float=0.1, max_depth: int=3) -> list[float]:
    prediction = np.full_like(y_train, np.mean(y_train))
    for _ in range(n_estimators):
        residuals = -(y_train - prediction)
        tree = Node()
        tree.fit(X_train, residuals, max_depth=max_depth)
        tree_predictions = np.array([tree.predict(x) for x in X_train])
        prediction += learning_rate * tree_predictions
        test_predictions = [tree.predict(x) for x in X_test]
    return [round(pred, 4) for pred in test_predictions]

