def root_mean_squared_log_error(actual: list[float], predicted: list[float]) -> float:
    if len(actual) != len(predicted):
        return -1
    for value in actual + predicted:
        if value < 0:
            return -1
    n = len(actual)
    diff_sum = sum(((np.log(1 + pred) - np.log(1 + act)) ** 2 for (act, pred) in zip(actual, predicted)))
    rmsle = np.sqrt(diff_sum / n)
    return round(rmsle, 4)

import numpy as np
def sigmoid(x, order: int=0):
    """Compute the logistic sigmoid or its derivatives.

    Args:
        x (float | int | list | np.ndarray): Input data. Can be a scalar, list, or NumPy array.
        order (int, optional): 0 = function value, 1 = first derivative,
            2 = second derivative. Defaults to 0.

    Returns:
        float | list: Result rounded to 4 decimals. Scalar input returns a float;
            vector/matrix input returns a Python list preserving the shape.
            If *order* is not 0, 1, or 2, the function returns -1.
    """

    def compute_sigmoid(arr):
        return 1 / (1 + np.exp(-arr))
    if order not in [0, 1, 2]:
        return -1
    input_is_scalar = isinstance(x, (int, float))
    arr = np.array(x)
    if order == 0:
        result = compute_sigmoid(arr)
    elif order == 1:
        sig = compute_sigmoid(arr)
        result = sig * (1 - sig)
    elif order == 2:
        sig = compute_sigmoid(arr)
        result = sig * (1 - sig) * (1 - 2 * sig)
    result = np.round(result, 4)
    if input_is_scalar:
        return float(result)
    else:
        return result.tolist()

import itertools
def apriori_frequent_itemsets(transactions: list[list[int]], min_sup: float) -> list[tuple]:
    """Find frequent itemsets with the Apriori algorithm.

    Parameters
    ----------
    transactions : list[list[Hashable]]
        List of transactions; each transaction is itself a list of items.
    min_sup : float
        Minimum support threshold expressed as a fraction (>0 and ≤1).

    Returns
    -------
    list[tuple]
        Frequent itemsets ordered by length and then lexicographically.
    """

    def calculate_support(itemset):
        return sum((1 for t in transactions if set(itemset).issubset(set(t)))) / len(transactions)

    def join_sets(itemsets, k):
        return set(itertools.chain.from_iterable(itertools.combinations(sorted(set(itertools.chain(*itemsets))), k)))

    def prune_candidates(candidates, itemsets):
        return {c for c in candidates if all((set(subset).union({c[i]}) in itemsets for i in range(len(c)) for subset in itertools.combinations(c, len(c) - 1)))}
    all_items = sorted(set(itertools.chain(*transactions)))
    L = [{item} for item in all_items if calculate_support([item]) >= min_sup]
    k = 2
    while True:
        C_k = join_sets(L[-1], k)
        pruned_candidates = prune_candidates(C_k, set().union(*L))
        frequent_itemsets = [c for c in pruned_candidates if calculate_support(c) >= min_sup]
        if not frequent_itemsets:
            break
        L.append(frequent_itemsets)
        k += 1
    return sorted(itertools.chain(*[sorted(i) for i in L]), key=lambda x: (len(x), x))

