def rbf_kernel(X: list[list[int | float]], Y: list[list[int | float]] | None=None, sigma: float | list[float] | None=None) -> list[list[float]]:
    """Compute the Radial Basis Function (RBF) kernel matrix.

    The function should follow the specifications given in the task
    description. It must return -1 on invalid input, otherwise a nested list
    containing the kernel matrix rounded to four decimal places.
    """
    X = np.array(X)
    if Y is not None:
        Y = np.array(Y)
    n_features = X.shape[1]
    if isinstance(sigma, (int, float)):
        if sigma <= 0:
            return -1
        sigma = np.ones(n_features) * sigma
    elif isinstance(sigma, list):
        sigma = np.array(sigma)
        if len(sigma) != n_features or np.any(sigma <= 0):
            return -1
    elif sigma is None:
        sigma = np.sqrt(n_features / 2) * np.ones(n_features)
    else:
        return -1
    if Y is not None and Y.shape[1] != n_features:
        return -1
    if Y is None:
        Y = X
    K = np.zeros((X.shape[0], Y.shape[0]))
    for i in range(X.shape[0]):
        for j in range(Y.shape[0]):
            diff = (X[i] - Y[j]) / sigma
            K[i, j] = np.exp(-0.5 * np.sum(diff ** 2))
    K_rounded = np.round(K, decimals=4).tolist()
    return K_rounded
