def euclidean_distance(point1, point2):
    """Calculate the Euclidean distance between two points."""
    return np.sqrt(np.sum((point1 - point2) ** 2))
def region_query(data, point_idx, eps):
    """Find all points in the dataset within a given radius (eps) of a specified point."""
    neighbors = []
    for (idx, point) in enumerate(data):
        if euclidean_distance(data[point_idx], point) < eps:
            neighbors.append(idx)
    return neighbors
def expand_cluster(data, labels, point_idx, cluster_id, eps, min_samples):
    """Expand a cluster starting from a core point."""
    seeds = region_query(data, point_idx, eps)
    if len(seeds) < min_samples:
        labels[point_idx] = -1
        return False
    else:
        for seed_idx in seeds:
            labels[seed_idx] = cluster_id
        while seeds:
            current_point = seeds.pop(0)
            current_neighbors = region_query(data, current_point, eps)
            if len(current_neighbors) >= min_samples:
                for neighbor_idx in current_neighbors:
                    if labels[neighbor_idx] == -1:
                        labels[neighbor_idx] = cluster_id
                    elif labels[neighbor_idx] >= 0:
                        continue
                    seeds.append(neighbor_idx)
        return True
