def regression_tree_predict(X_train: list[float | int], y_train: list[float | int], X_test: list[float | int], max_depth: int=3) -> list[float]:
    """Fit a univariate regression tree of depth *max_depth* and predict.

    Parameters
    ----------
    X_train : list[float | int]
        1-D feature values for the training set.
    y_train : list[float | int]
        Continuous target values for the training set.
    X_test : list[float | int]
        1-D feature values for which predictions are required.
    max_depth : int, default 3
        Maximum depth of the binary tree (root has depth 0).

    Returns
    -------
    list[float]
        Predictions for every element in *X_test*.
    """
    if not X_train:
        return []

    def sse(y):
        mean_y = np.mean(y)
        return np.sum((y - mean_y) ** 2)

    def split_node(index, value, X, y):
        (left_X, right_X) = ([], [])
        (left_y, right_y) = ([], [])
        for i in range(len(X)):
            if X[i] < value:
                left_X.append(X[i])
                left_y.append(y[i])
            else:
                right_X.append(X[i])
                right_y.append(y[i])
        return (left_X, right_X, left_y, right_y)

    def get_split(X, y, depth):
        (b_index, b_value, b_score, b_groups) = (999, 999, 999, None)
        for index in range(len(X)):
            for value in set(X):
                groups = split_node(index, value, X, y)
                gini = sum((sse(group[1]) for group in zip(groups[::2], groups[1::2])))
                if gini < b_score:
                    (b_index, b_value, b_score, b_groups) = (index, value, gini, groups)
        return {'index': b_index, 'value': b_value, 'groups': b_groups}

    def to_terminal(y):
        return np.mean(y)

    def split(node, max_depth, depth=1):
        (left, right) = node['groups']
        del node['groups']
        if not left[0] or not right[0]:
            node['left'] = node['right'] = to_terminal(left[1] + right[1])
            return
        if depth >= max_depth:
            (node['left'], node['right']) = (to_terminal(left[1]), to_terminal(right[1]))
            return
        if len(left[0]) <= 1 or len(right[0]) <= 1:
            node['left'] = node['right'] = to_terminal(left[1] + right[1])
            return
        node['left'] = get_split(left[0], left[1], depth + 1)
        split(node['left'], max_depth, depth + 1)
        node['right'] = get_split(right[0], right[1], depth + 1)
        split(node['right'], max_depth, depth + 1)

    def predict(node, value):
        if value < node['value']:
            if isinstance(node['left'], dict):
                return predict(node['left'], value)
            else:
                return node['left']
        elif isinstance(node['right'], dict):
            return predict(node['right'], value)
        else:
            return node['right']
    root = get_split(X_train, y_train, max_depth)
    split(root, max_depth)
    return [predict(root, value) for value in X_test]
