def polynomial_features(X: list[list[int | float]], degree: int) -> list[list[float]]:
    """Generate a full polynomial feature matrix up to a given degree."""
    if degree < 0:
        return -1
    X = np.array(X)
    (n_samples, n_features) = X.shape
    powers_list = list(combinations_with_replacement(range(n_features), degree))
    powers = np.zeros((len(powers_list), n_features))
    for (i, power_tuple) in enumerate(powers_list):
        for j in range(len(power_tuple)):
            powers[i][power_tuple[j]] += 1
    final_powers = np.vstack((np.zeros(n_features), powers))
    X_poly = np.apply_along_axis(lambda x: np.prod(x[None, :] ** final_powers, axis=1), 1, X)
    return X_poly.tolist()

