import math
def detect_outliers(data: np.ndarray, n_trees: int, sample_size: int, epsilon: float) -> list[int]:
    """Tiny Isolation Forest implementation.

    Args:
        data: 2-D NumPy array where each row is an observation and each column a feature.
        n_trees: Number of random isolation trees that will be grown.
        sample_size: Number of samples drawn (without replacement) to grow every tree.
        epsilon: Fraction (0 ≤ ε < 1) of observations that are considered outliers.

    Returns:
        A list with the zero-based indices of the detected outliers, sorted increasingly.
    """
    np.random.seed(42)
    (n_samples, n_features) = data.shape
    if sample_size >= n_samples:
        sample_size = n_samples
    height_limit = math.ceil(math.log2(sample_size))

    def _build_tree(X):
        if X.shape[0] <= 1 or height_limit == 0:
            return (X, None, None)
        feature_idx = np.random.randint(n_features)
        (min_val, max_val) = (X[:, feature_idx].min(), X[:, feature_idx].max())
        if min_val == max_val:
            return (X, None, None)
        threshold = np.random.uniform(min_val, max_val)
        left = X[X[:, feature_idx] < threshold]
        right = X[X[:, feature_idx] >= threshold]
        return (None, _build_tree(left), _build_tree(right))

    def _path_length(point, tree, depth=1):
        if not tree:
            return depth + _c(tree[0].shape[0]) if len(tree) > 0 else depth
        (node, left, right) = tree
        feature_idx = np.random.randint(n_features)
        if point[feature_idx] < left[0][feature_idx]:
            return _path_length(point, left, depth + 1)
        else:
            return _path_length(point, right, depth + 1)

    def _c(n):
        if n <= 1:
            return 0
        else:
            return 2 * math.log(n - 1) + 0.5772156649 - 2 * (n - 1) / n
    phi = 2 * math.log(n_samples - 1) - 2 * (n_samples - 1) / n_samples
    trees = [_build_tree(np.random.choice(n_samples, sample_size, replace=False)) for _ in range(n_trees)]
    path_lengths = np.array([[_path_length(data[i], tree) for tree in trees] for i in range(n_samples)])
    average_path_lengths = path_lengths.mean(axis=1)
    anomaly_scores = 2 ** (-average_path_lengths / phi)
    threshold = np.percentile(anomaly_scores, 100 * (1 - epsilon))
    outliers = np.where(anomaly_scores > threshold)[0]
    return sorted(outliers.tolist())

