from collections import defaultdict
import numpy as np
def maxent_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, epsilon: float=0.001, n_iter: int=100) -> list[int]:

    def empirical_distribution(X, y):
        emp_dist = defaultdict(int)
        for (features, label) in zip(X, y):
            for (i, val) in enumerate(features):
                key = (i, val, label)
                emp_dist[key] += 1
        return {k: v / len(y) for (k, v) in emp_dist.items()}

    def log_sum_exp(values):
        if len(values) == 0:
            return -float('inf')
        max_val = max(values)
        return max_val + math.log(sum((math.exp(v - max_val) for v in values)))

    def compute_z(x, weights):
        return log_sum_exp([sum((weights.get((i, val, label), 0) for (i, val) in enumerate(x))) for label in set(y_train)])

    def update_weights(weights, expectations, empirical):
        new_weights = {}
        for (key, exp_value) in expectations.items():
            if key in empirical:
                new_weights[key] = weights.get(key, 0) + math.log(empirical[key] / exp_value)
        return new_weights
    weights = defaultdict(float)
    emp_dist = empirical_distribution(X_train, y_train)
    for _ in range(n_iter):
        expectations = defaultdict(float)
        for (features, label) in zip(X_train, y_train):
            z = compute_z(features, weights)
            for (i, val) in enumerate(features):
                key = (i, val, label)
                expectations[key] += math.exp(sum((weights.get((i, val, l), 0) for l in set(y_train))) - z) / len(y_train)
        new_weights = update_weights(weights, expectations, emp_dist)
        if all((abs(new_weights.get(k, 0) - weights[k]) < epsilon for k in weights.keys())):
            break
        weights.update(new_weights)
    predictions = []
    for x in X_test:
        scores = {}
        for label in set(y_train):
            scores[label] = sum((weights.get((i, val, label), 0) for (i, val) in enumerate(x))) - compute_z(x, weights)
        predictions.append(max(scores, key=scores.get))
    return predictions

import numpy as np
def euclidean_distance(point1, point2):
    """Calculate Euclidean distance between two points."""
    return np.sqrt(np.sum((point1 - point2) ** 2))
def compute_distance_matrix(data):
    """Compute pairwise Euclidean distance matrix for the given dataset."""
    num_samples = len(data)
    dist_matrix = np.zeros((num_samples, num_samples))
    for i in range(num_samples):
        for j in range(i + 1, num_samples):
            dist = euclidean_distance(data[i], data[j])
            dist_matrix[i, j] = dist
            dist_matrix[j, i] = dist
    return dist_matrix
def k_distance(dist_matrix, k):
    """Find k-distance and k-neighbors for each sample based on the distance matrix."""
    k_dist = []
    k_neighbors = []
    for row in dist_matrix:
        sorted_indices = np.argsort(row)
        k_dist.append(row[sorted_indices[k]])
        k_neighbors.append(sorted_indices[:k].tolist())
    return (k_dist, k_neighbors)
def reachability_distance(dist_matrix, k_dist, p_index, q_index):
    """Calculate the reachability distance between two points p and q."""
    return max(k_dist[q_index], dist_matrix[p_index, q_index])
def local_reachability_density(dist_matrix, k_dist, k_neighbors, p_index):
    """Calculate LRD for a sample p."""
    reach_dists_sum = sum((reachability_distance(dist_matrix, k_dist, p_index, q_index) for q_index in k_neighbors[p_index]))
    return len(k_neighbors[p_index]) / reach_dists_sum
def local_outlier_factor(data, k, epsilon=1.5):
    """Compute LOF scores and identify outliers for the given dataset."""
    dist_matrix = compute_distance_matrix(data)
    (k_dist, k_neighbors) = k_distance(dist_matrix, k)
    lrd = [local_reachability_density(dist_matrix, k_dist, k_neighbors, i) for i in range(len(data))]
    lof_scores = []
    outlier_indices = []
    for p_index in range(len(data)):
        avg_lrd_neighbors = sum((lrd[q_index] for q_index in k_neighbors[p_index])) / len(k_neighbors[p_index])
        lof_score = avg_lrd_neighbors / lrd[p_index]
        lof_scores.append(round(lof_score, 4))
        if lof_score > epsilon:
            outlier_indices.append(p_index)
    return (lof_scores, sorted(outlier_indices))

import numpy as np
def kmeans_centroids(X: 'np.ndarray', k: int, max_iters: int=100) -> list[list[float]]:
    centroids = X[:k].copy()
    for _ in range(max_iters):
        clusters = [[] for _ in range(k)]
        for sample in X:
            distances = np.sum((centroids - sample) ** 2, axis=1)
            closest_centroid_idx = np.argmin(distances)
            clusters[closest_centroid_idx].append(sample)
        new_centroids = np.zeros_like(centroids)
        for (i, cluster) in enumerate(clusters):
            if cluster:
                new_centroids[i] = np.mean(cluster, axis=0)
            else:
                new_centroids[i] = centroids[i]
        if np.all(np.linalg.norm(new_centroids - centroids, axis=1) < 0.0001):
            break
        centroids = new_centroids
    sorted_rounded_centroids = sorted([list(np.round(centroid, 4)) for centroid in centroids])
    return sorted_rounded_centroids
