import numpy as np
def glm_links():
    """Construct and return standard GLM link functions.

    Returns
    -------
    dict
        A three-entry dictionary (identity, log, logit) where each entry is a
        dictionary containing callables for the link, its inverse and its
        derivative with respect to μ. All functions must work with numpy
        scalars as well as 1-D/2-D numpy arrays via element-wise operations.
    """

    def identity(u):
        return u

    def log(u):
        return np.log(u)

    def logit(u):
        return np.log(u / (1 - u))

    def inv_logit(v):
        return 1 / (1 + np.exp(-v))

    def deriv_identity(u):
        return 1

    def deriv_log(u):
        return 1 / u

    def deriv_logit(v):
        exp_v = np.exp(v)
        return exp_v / (1 + exp_v) ** 2
    links = {'identity': {'link': identity, 'inv_link': inv_logit, 'deriv': deriv_identity}, 'log': {'link': log, 'inv_link': lambda x: np.exp(x), 'deriv': deriv_log}, 'logit': {'link': logit, 'inv_link': inv_logit, 'deriv': deriv_logit}}
    return links
