import numpy as np
def softplus(x, deriv: bool=False):
    """
    Implement the SoftPlus activation function and its derivative.

    :param x: A scalar, list, or NumPy array of real numbers
    :param deriv: Whether to compute the derivative of the SoftPlus function
    :return: The processed values as a list, or a single float if the input was a scalar
    """
    x = np.array(x)

    def log1px(x):
        return np.where(x < 709, np.log1p(np.exp(x)), x - 1 / (1 + np.exp(-np.abs(x))))

    def softplus_func(x):
        return log1px(x)

    def softplus_derivative(x):
        return np.reciprocal(1 + np.exp(-x))
    if deriv:
        return softplus_derivative(x).round(4)
    else:
        return softplus_func(x).round(4)
