from collections import Counter, defaultdict
def sorted_tuple(item):
    """Sort items in each item-set."""
    return tuple(sorted(item))
def fp_growth(transactions: list[list[str]], min_support: int) -> list[list[str]]:
    """Discover every frequent item-set in *transactions* with FP-Growth.

    A *transaction* is represented by a list of items (strings).  `min_support`
    is the minimum number of transactions an item-set has to appear in.  The
    function returns **all** frequent item-sets where
        support(itemset) >= min_support.

    The result must be deterministic:
      • Inside each item-set the items have to be sorted alphabetically.
      • The outer list has to be sorted by `(len(itemset), itemset)`.
    If *transactions* is empty or no item-set meets the threshold return an
    empty list.
    """
    item_counts = Counter()
    for transaction in transactions:
        for item in transaction:
            item_counts[item] += 1
    frequent_items = {item: count for (item, count) in item_counts.items() if count >= min_support}
    if not frequent_items:
        return []
    root = defaultdict(int)
    for (item, _) in frequent_items.items():
        root[item] += 1

    def build_fptree(frequent_items, parent):
        fptree = defaultdict(int)
        for (item, count) in frequent_items.items():
            fptree[item] += count
            fptree[parent] += count
        return fptree

    def split_tree(fptree):
        for (item, count) in fptree.items():
            if count == 0:
                continue
            yield ((item, count), split_tree(copy.deepcopy(fptree) - {item}))

    def find_prefix_path(fptree, path):
        current = fptree
        for (item, count) in path:
            while item not in current:
                current = current[path]
            current = current[item]
        return current

    def mine_conditional_trees(fptree, parent, prefix):
        path = find_prefix_path(fptree, prefix)
        cond_fptrees = {}
        for (item, count) in split_tree(path):
            (child_fptree, remaining) = build_fptree(frequent_items, item)
            if remaining:
                cond_fptrees[item] = mine_conditional_trees(child_fptree, item, prefix + [item])
        return cond_fptrees
    fptree = build_fptree(frequent_items, '')
    cond_fptrees = mine_conditional_trees(fptree, '', [])
    final_frequent_itemsets = []

    def collect_sets(fptree, prefix, depth):
        if depth > 0:
            for (item, count) in fptree.items():
                if count == 0:
                    continue
                collect_sets(cond_fptrees[item], prefix + [item], depth - 1)
        else:
            final_frequent_itemsets.append(prefix)
    collect_sets(fptree, [], len(frequent_items))
    final_frequent_itemsets.sort(key=sorted_tuple)
    return final_frequent_itemsets
