def escape_fire_maze(grid: list[str]) -> int:

    def simulate_fire_spread():
        n = len(grid)
        visited = [[False] * n for _ in range(n)]
        queue = deque()
        for i in range(n):
            for j in range(n):
                if grid[i][j] == 'F':
                    queue.append((i, j))
                    visited[i][j] = True
        steps = 0
        directions = [(0, 1), (1, 0), (-1, 0), (0, -1)]
        while queue and steps < n:
            for _ in range(len(queue)):
                (x, y) = queue.popleft()
                if grid[x][y] == 'F':
                    grid[x][y] = '#'
                for (dx, dy) in directions:
                    (nx, ny) = (x + dx, y + dy)
                    if 0 <= nx < n and 0 <= ny < n and (not visited[nx][ny]) and (grid[nx][ny] == '.'):
                        visited[nx][ny] = True
                        queue.append((nx, ny))
            steps += 1
        return steps

    def bfs(start_x, start_y):
        n = len(grid)
        visited = [[False] * n for _ in range(n)]
        queue = deque([(start_x, start_y, 0)])
        while queue:
            (x, y, step) = queue.popleft()
            if x == n - 1 and y == n - 1:
                return step
            visited[x][y] = True
            for (dx, dy) in [(0, 1), (1, 0), (-1, 0), (0, -1)]:
                (nx, ny) = (x + dx, y + dy)
                if 0 <= nx < n and 0 <= ny < n and (not visited[nx][ny]):
                    if grid[nx][ny] != '#' and grid[nx][ny] != 'F':
                        visited[nx][ny] = True
                        queue.append((nx, ny, step + 1))
        return -1
    n = len(grid)
    if n == 1 and grid[0][0] == '.':
        return 0
    fire_cells = [(i, j) for i in range(n) for j in range(n) if grid[i][j] == 'F']
    if any(fire_cells):
        min_steps = simulate_fire_spread()
        if min_steps >= n:
            return -1
    for i in range(n):
        for j in range(n):
            if grid[i][j] == 'F':
                grid[i][j] = '#'
    result = bfs(0, 0)
    return result
