import numpy as np
import numpy as np
def metric(name: str):

    def euclidean(x, y):
        if not (isinstance(x, (list, tuple, np.ndarray)) and isinstance(y, (list, tuple, np.ndarray))):
            return -1
        x = np.array(x)
        y = np.array(y)
        if len(x) != len(y) or any((not isinstance(e, (int, float)) for e in x + y)):
            return -1
        dist = np.sqrt(np.sum((x - y) ** 2))
        return round(dist, 4)

    def manhattan(x, y):
        if not (isinstance(x, (list, tuple, np.ndarray)) and isinstance(y, (list, tuple, np.ndarray))):
            return -1
        x = np.array(x)
        y = np.array(y)
        if len(x) != len(y) or any((not isinstance(e, (int, float)) for e in x + y)):
            return -1
        dist = np.sum(np.abs(x - y))
        return round(dist, 4)

    def chebyshev(x, y):
        if not (isinstance(x, (list, tuple, np.ndarray)) and isinstance(y, (list, tuple, np.ndarray))):
            return -1
        x = np.array(x)
        y = np.array(y)
        if len(x) != len(y) or any((not isinstance(e, (int, float)) for e in x + y)):
            return -1
        dist = np.max(np.abs(x - y))
        return round(dist, 4)

    def cosine(x, y):
        if not (isinstance(x, (list, tuple, np.ndarray)) and isinstance(y, (list, tuple, np.ndarray))):
            return -1
        x = np.array(x)
        y = np.array(y)
        if len(x) != len(y) or any((not isinstance(e, (int, float)) for e in x + y)) or x == 0 or (y == 0):
            return -1
        dot_product = np.dot(x, y)
        norm_x = np.linalg.norm(x)
        norm_y = np.linalg.norm(y)
        cosine_sim = dot_product / (norm_x * norm_y)
        cosine_dist = 1 - cosine_sim
        return round(cosine_dist, 4)
    if name == 'euclidean':
        return euclidean
    elif name == 'manhattan':
        return manhattan
    elif name == 'chebyshev':
        return chebyshev
    elif name == 'cosine':
        return cosine
    else:
        return -1
