import itertools
def fp_growth(transactions, min_sup):
    """Mine all frequent item-sets using the FP-Growth algorithm.

    Parameters
    ----------
    transactions : Iterable[Iterable[Hashable]]
        A collection of transactions.  Each transaction is an iterable containing hashable items.
    min_sup : int
        Minimum number of occurrences an item-set must have to be considered frequent.

    Returns
    -------
    list[tuple]
        All frequent item-sets sorted 1) by length, 2) lexicographically.  Every
        tuple itself is sorted lexicographically.
    """

    def load_transactions_to_counter(transactions):
        """Load transactions into a Counter of item counts."""
        item_counts = Counter()
        for transaction in transactions:
            item_counts.update(transaction)
        return item_counts

    def construct_tree(item_counts, min_support):
        """Create an FP-Tree with item counts and minimum support."""
        frequent_items = {item: freq for (item, freq) in item_counts.items() if freq >= min_support}
        return _construct_tree(frequent_items)

    def _construct_tree(items, depth=0):
        """Helper function to recursively build the FP-Tree."""
        if not items:
            return None
        (item, freq) = max(items.items(), key=lambda x: x[1])
        del items[item]
        tree = {'item': item, 'freq': freq, 'children': [], 'pattern_base': []}
        for (child_item, _) in items.items():
            tree['pattern_base'].append((child_item, freq))
            tree['children'].append(_construct_tree({child_item: freq}, depth + 1))
        return tree

    def count_paths(tree, prefix=None):
        """Count paths from root to leaf nodes."""
        if prefix is None:
            prefix = []
        if tree['item'] is not None:
            pattern = tuple(sorted(prefix + [tree['item']]))
            yield pattern
            if tree['children']:
                for path in count_paths(tree['children'][0], prefix + [tree['item']]):
                    yield path
        if tree['children']:
            for child in tree['children']:
                yield from count_paths(child)

    def mine_frequent_sets(tree, min_support, prefix_count):
        """Recursively mine frequent sets from the constructed FP-Tree."""
        if tree['item'] is not None:
            if tree['freq'] >= min_support:
                for subset in itertools.combinations(tree['pattern_base'], len(tree['pattern_base'])):
                    yield tuple(sorted(subset))
            for child in tree['children']:
                yield from mine_frequent_sets(child, min_support, prefix_count)
        if tree['children']:
            for child in tree['children']:
                yield from mine_frequent_sets(child, min_support, prefix_count + 1)
    item_counts = load_transactions_to_counter(transactions)
    tree = construct_tree(item_counts, min_support)
    patterns = []
    for pattern in count_paths(tree):
        if len(pattern) > 1:
            patterns.append(pattern)
    return sorted((sorted(t) for t in patterns))
