def determinant_4x4(matrix: list[list[int | float]]) -> float:

    def sub_matrix(matrix: list[list[int | float]], row: int, col: int) -> list[list[int | float]]:
        """Return the sub-matrix after removing the specified row and column."""
        return [[matrix[r][c] for c in range(len(matrix)) if c != col] for r in range(len(matrix)) if r != row]

    def determinant(matrix: list[list[int | float]]) -> float:
        """Calculate the determinant of a matrix using recursion."""
        size = len(matrix)
        if size == 1:
            return matrix[0][0]
        elif size == 2:
            return matrix[0][0] * matrix[1][1] - matrix[0][1] * matrix[1][0]
        else:
            det = 0
            for col in range(size):
                sub_det = determinant(sub_matrix(matrix, 0, col))
                det += (-1) ** col * matrix[0][col] * sub_det
            return det
    if len(matrix) != 4 or any((len(row) != 4 for row in matrix)):
        raise ValueError('The matrix must be 4x4.')
    return determinant(matrix)
