from collections import Counter
def entropy(class_probabilities):
    """Calculate the entropy from a list of class probabilities."""
    return -sum((p * math.log2(p) for p in class_probabilities if p != 0))
def class_probabilities(examples, target_attr):
    """Calculate the probabilities of each class in the dataset."""
    counts = Counter((example[target_attr] for example in examples))
    total = len(examples)
    return [count / total for count in counts.values()]
def partition_by_attribute_value(examples, attribute, value):
    """Return a subset of examples where the attribute has the specified value."""
    return [example for example in examples if example[attribute] == value]
def split_information(examples, attribute):
    """Calculate the split information for a given attribute."""
    values = set((example[attribute] for example in examples))
    weighted_entropy = sum((len(partition) / len(examples) * entropy(class_probabilities(partition, 'label')) for value in values for partition in [partition_by_attribute_value(examples, attribute, value)]))
    return weighted_entropy
def majority_vote(examples, target_attr):
    """Return the most common value for a given attribute."""
    counts = Counter((example[target_attr] for example in examples))
    return counts.most_common(1)[0][0]
def learn_decision_tree(examples: list[dict], attributes: list[str], target_attr: str) -> dict:
    if not examples or not attributes:
        return {'leaf': majority_vote(examples, target_attr)}
    unique_labels = {example[target_attr] for example in examples}
    if len(unique_labels) == 1:
        return {'leaf': unique_labels.pop()}
    base_entropy = entropy(class_probabilities(examples, target_attr))
    information_gains = {attribute: base_entropy - split_information(examples, attribute) for attribute in attributes}
    best_attribute = max(information_gains, key=information_gains.get)
    remaining_attributes = [attr for attr in attributes if attr != best_attribute]
    tree = {best_attribute: {}}
    for value in set((example[best_attribute] for example in examples)):
        subset = partition_by_attribute_value(examples, best_attribute, value)
        subtree = learn_decision_tree(subset, remaining_attributes, target_attr)
        tree[best_attribute][value] = subtree
    return tree
