import numpy as np
def pegasos_kernel_svm(data: np.ndarray, labels: np.ndarray, kernel='linear', lambda_val=0.01, iterations=100, sigma=1.0):
    """
    Train a kernel SVM classifier using a deterministic version of the Pegasos algorithm.

    Parameters:
    - data: 2D NumPy array where each row is a data sample and each column is a feature.
    - labels: 1D NumPy array where each entry corresponds to the label of the sample.
    - kernel: Type of kernel to use ('linear' or 'rbf').
    - lambda_val: Regularization parameter.
    - iterations: Number of iterations for training.
    - sigma: Parameter for RBF kernel.

    Returns:
    - alpha: List of alpha coefficients.
    - b: Bias term.
    """
    (n_samples, n_features) = data.shape
    alpha = np.zeros(n_samples)
    b = 0

    def linear_kernel(x1, x2):
        return np.dot(x1, x2)

    def rbf_kernel(x1, x2, sigma):
        return np.exp(-np.linalg.norm(x1 - x2) ** 2 / (2 * sigma ** 2))
    if kernel == 'linear':
        K = np.zeros((n_samples, n_samples))
        for i in range(n_samples):
            for j in range(n_samples):
                K[i, j] = linear_kernel(data[i], data[j])
    elif kernel == 'rbf':
        K = np.zeros((n_samples, n_samples))
        for i in range(n_samples):
            for j in range(n_samples):
                K[i, j] = rbf_kernel(data[i], data[j], sigma)
    for t in range(1, iterations + 1):
        eta = 1 / (lambda_val * t)
        for i in range(n_samples):
            prediction = np.sign(np.sum(alpha * labels * K[:, i]) + b)
            if labels[i] * prediction < 1:
                alpha[i] = alpha[i] + 1
        alpha = (1 - eta * lambda_val) * alpha
        support_vectors = alpha > 0
        b = b + eta * (1 - np.sum(alpha[support_vectors] * labels[support_vectors] * K[support_vectors, :].dot(labels)))
    alpha = np.round(alpha, 4).tolist()
    b = np.round(b, 4)
    return (alpha, b)
