import numpy as np
def compute_tf_idf(corpus, query):
    if not corpus:
        raise ValueError('The corpus cannot be empty.')

    def compute_tf(doc):
        tf_dict = {}
        doc_length = len(doc)
        for word in doc:
            if word in tf_dict:
                tf_dict[word] += 1
            else:
                tf_dict[word] = 1
        for word in tf_dict:
            tf_dict[word] /= doc_length
        return tf_dict

    def compute_df(corpus):
        df_dict = {}
        for doc in corpus:
            unique_words = set(doc)
            for word in unique_words:
                if word in df_dict:
                    df_dict[word] += 1
                else:
                    df_dict[word] = 1
        return df_dict

    def compute_idf(df_dict, n_docs):
        idf_dict = {}
        for (word, df) in df_dict.items():
            idf_dict[word] = np.log((n_docs + 1) / (df + 1)) + 1
        return idf_dict

    def compute_tfidf(tf_dict, idf_dict):
        tfidf_dict = {}
        for (word, tf) in tf_dict.items():
            if word in idf_dict:
                tfidf_dict[word] = tf * idf_dict[word]
            else:
                tfidf_dict[word] = 0
        return tfidf_dict
    n_docs = len(corpus)
    df_dict = compute_df(corpus)
    idf_dict = compute_idf(df_dict, n_docs)
    tfidf_scores = []
    for doc in corpus:
        tf_dict = compute_tf(doc)
        tfidf_dict = compute_tfidf(tf_dict, idf_dict)
        doc_scores = [tfidf_dict.get(word, 0) for word in query]
        tfidf_scores.append(doc_scores)
    tfidf_scores = np.array(tfidf_scores).round(4).tolist()
    return tfidf_scores
