def calculate_brightness(img):
    if not img or not img[0]:
        return -1
    row_length = len(img[0])
    total_brightness = 0
    pixel_count = 0
    for row in img:
        if len(row) != row_length:
            return -1
        for pixel in row:
            if not 0 <= pixel <= 255:
                return -1
            total_brightness += pixel
            pixel_count += 1
    if pixel_count == 0:
        return -1
    average_brightness = total_brightness / pixel_count
    return round(average_brightness, 2)
