def gelu(x):
    return 0.5 * x * (1 + np.tanh(np.sqrt(2 / np.pi) * (x + 0.044715 * x ** 3)))
def softmax(x):
    e_x = np.exp(x - np.max(x, axis=-1, keepdims=True))
    return e_x / e_x.sum(axis=-1, keepdims=True)
def layer_norm(x, g, b, eps=1e-05):
    mean = np.mean(x, axis=-1, keepdims=True)
    variance = np.var(x, axis=-1, keepdims=True)
    return g * (x - mean) / np.sqrt(variance + eps) + b
def multi_head_attention(q, k, v, n_head, mask=None):
    d_k = q.shape[-1] // n_head
    q = q.reshape(q.shape[0], n_head, d_k).transpose(1, 0, 2)
    k = k.reshape(k.shape[0], n_head, d_k).transpose(1, 0, 2)
    v = v.reshape(v.shape[0], n_head, d_k).transpose(1, 0, 2)
    scores = np.matmul(q, k.transpose(0, 2, 1)) / np.sqrt(d_k)
    if mask is not None:
        scores += mask * -1000000000.0
    weights = softmax(scores)
    output = np.matmul(weights, v).transpose(1, 0, 2).reshape(q.shape[0], -1)
    return output
def feed_forward(x, w1, b1, w2, b2):
    hidden = gelu(np.dot(x, w1) + b1)
    output = np.dot(hidden, w2) + b2
    return output
def gen_text(prompt: str, n_tokens_to_generate: int=40):
    (encoder, hparams, params) = load_encoder_hparams_and_params()
    n_ctx = hparams['n_ctx']
    n_head = hparams['n_head']
    input_ids = encoder.encode(prompt)
    input_len = len(input_ids)
    token_embeddings = params['wte'][input_ids]
    pos_embeddings = params['wpe'][:input_len]
    x = token_embeddings + pos_embeddings
    x = layer_norm(x, params['ln_f']['g'], params['ln_f']['b'])
    for _ in range(n_tokens_to_generate):
        q = k = v = x
        attn_output = multi_head_attention(q, k, v, n_head)
        ff_output = feed_forward(attn_output, np.random.rand(10, 10), np.zeros(10), np.random.rand(10, 3), np.zeros(3))
        x = layer_norm(ff_output, params['ln_f']['g'], params['ln_f']['b'])
        logits = np.dot(x[-1], params['wte'].T)
        next_token_id = np.argmax(logits)
        input_ids.append(next_token_id)
        token_embeddings = params['wte'][input_ids[-1]]
        pos_embeddings = params['wpe'][len(input_ids) - 1]
        x = np.vstack((x, token_embeddings + pos_embeddings))
    generated_text = encoder.decode(input_ids)
    return generated_text
