def find_frequent_itemsets(transactions: List[List[Hashable]], minsup: float) -> Dict[Tuple[Hashable, ...], int]:
    """Return all frequent itemsets in **transactions**.

    Args:
        transactions: List where each element represents a transaction and is
                       itself a list of items (hashable Python objects).
        minsup:       Minimum support given as a fraction between 0 and 1.

    Returns:
        A dictionary that maps every frequent itemset (stored as a tuple of
        sorted items) to its absolute support count.  If no itemset reaches
        the threshold the function must return an empty dictionary.
    """
    min_support_count = math.ceil(minsup * len(transactions))
    itemset_supports = Counter()

    def generate_itemsets(itemset_size):
        if itemset_size == 1:
            itemset_supports.update((frozenset({item}) for transaction in transactions for item in transaction))
        else:
            prev_itemsets = {frozenset(itemset) for itemset in itemset_supports if len(itemset) == itemset_size - 1}
            for itemset in prev_itemsets:
                for transaction in transactions:
                    if itemset.issubset(set(transaction)):
                        candidates = set(transaction) - itemset
                        for candidate in candidates:
                            new_itemset = frozenset(itemset | {candidate})
                            if new_itemset not in itemset_supports:
                                itemset_supports[new_itemset] = 0
                            itemset_supports[new_itemset] += 1
    generate_itemsets(1)
    itemset_size = 2
    while True:
        generate_itemsets(itemset_size)
        if all((count < min_support_count for count in itemset_supports.values())):
            break
        itemset_size += 1
    frequent_itemsets = {tuple(sorted(itemset)): count for (itemset, count) in itemset_supports.items() if count >= min_support_count}
    return frequent_itemsets
