def cosine_similarity(v1, v2):
    if np.std(v1) > 0.001:
        v1 = v1 - np.mean(v1)
    if np.std(v2) > 0.001:
        v2 = v2 - np.mean(v2)
    return np.dot(v1, v2) / (np.linalg.norm(v1) * np.linalg.norm(v2))
def pearson_similarity(v1, v2):
