def knn_recommend(data: np.ndarray, user_ind: int, k: int, criterion: str='cosine') -> list[int]:
    (n_users, n_items) = data.shape
    S = np.zeros((n_items, n_items))
    for i in range(n_items):
        for j in range(i, n_items):
            common_users = (data[:, i] > 0) & (data[:, j] > 0)
            if np.any(common_users):
                v1 = data[common_users, i]
                v2 = data[common_users, j]
                if criterion == 'cosine':
                    sim = cosine_similarity(v1, v2)
                elif criterion == 'pearson':
                    sim = pearson_similarity(v1, v2)
                else:
                    raise ValueError("Invalid criterion. Choose 'cosine' or 'pearson'.")
                S[i, j] = S[j, i] = sim
    rated_items = np.where(data[user_ind] > 0)[0]
    unrated_items = np.array([i for i in range(n_items) if i not in rated_items])
    scores = {}
    for t in unrated_items:
        numerator = sum((data[user_ind, i] * S[t, i] for i in rated_items))
        denominator = sum((abs(S[t, i]) for i in rated_items))
        scores[t] = numerator / denominator if denominator != 0 else 0
    top_k_items = sorted(scores.items(), key=lambda x: (-x[1], x[0]))[:k]
    return [item[0] for item in top_k_items]

