def softplus(x, deriv: bool=False):
    """Compute the SoftPlus activation function or its derivative (sigmoid) for each element in x.

    Args:
    - x: A scalar, Python list, or NumPy array of real numbers.
    - deriv: A boolean flag. If True, return the derivative (sigmoid) of x. Default is False.

    Returns:
    - A Python list (or a single float if x is a scalar) of the SoftPlus values or their derivatives,
      rounded to the nearest 4th decimal.
    """
    x_array = np.array(x)
    if deriv:
        result = 1 / (1 + np.exp(-x_array))
    else:
        result = np.log1p(np.exp(-np.abs(x_array))) + np.maximum(x_array, 0)
    result_rounded = np.round(result, 4)
    if np.isscalar(x):
        return float(result_rounded)
    else:
        return result_rounded.tolist()

