import numpy as np
def gini_impurity(labels):
    """Calculate the Gini Impurity for a list of labels."""
    (_, counts) = np.unique(labels, return_counts=True)
    probabilities = counts / len(labels)
    return 1 - np.sum(probabilities ** 2)
def best_split(X, y):
    """Find the best split for the given data using Gini impurity."""
    best_gini_gain = -1
    best_feature = None
    best_threshold = None
    parent_gini = gini_impurity(y)
    (n_samples, n_features) = X.shape
    for feature in range(n_features):
        sorted_indices = np.argsort(X[:, feature])
        sorted_X = X[sorted_indices, feature]
        sorted_y = y[sorted_indices]
        for i in range(1, n_samples):
            if sorted_X[i] != sorted_X[i - 1]:
                threshold = (sorted_X[i - 1] + sorted_X[i]) / 2
                left_labels = sorted_y[:i]
                right_labels = sorted_y[i:]
                left_gini = gini_impurity(left_labels)
                right_gini = gini_impurity(right_labels)
                weighted_gini = (len(left_labels) * left_gini + len(right_labels) * right_gini) / n_samples
                gini_gain = parent_gini - weighted_gini
                if gini_gain > best_gini_gain:
                    best_gini_gain = gini_gain
                    best_feature = feature
                    best_threshold = threshold
    return (best_feature, best_threshold, best_gini_gain)
def majority_vote(labels):
    """Return the majority class label."""
    (unique, counts) = np.unique(labels, return_counts=True)
    return unique[np.argmax(counts)]
