def build_tree(X, y, max_depth=None, current_depth=0):
    """Recursively build the decision tree."""
    if max_depth is not None and current_depth >= max_depth:
        return Node(value=majority_vote(y))
    if len(np.unique(y)) == 1:
        return Node(value=y[0])
    (best_feature, best_threshold, _) = best_split(X, y)
    if best_feature is None:
        return Node(value=majority_vote(y))
    left_indices = X[:, best_feature] <= best_threshold
    right_indices = X[:, best_feature] > best_threshold
    left_child = build_tree(X[left_indices], y[left_indices], max_depth, current_depth + 1)
    right_child = build_tree(X[right_indices], y[right_indices], max_depth, current_depth + 1)
    return Node(feature=best_feature, threshold=best_threshold, left=left_child, right=right_child)
