import numpy as np
from typing import Iterable, Callable
def metric(name: str) -> Callable[[Iterable, Iterable], float]:
    """Factory producing a validated distance function.

    The function creates and returns a callable *d* that computes one of four
    classical distances (Euclidean, Manhattan, Chebyshev, Cosine) between two
    numeric vectors.  All numeric outputs are rounded to four decimal places.

    Validation rules inside the returned callable:
    * Both arguments must be one-dimensional, non-empty, equal-length numeric
      iterables (list, tuple or NumPy array).
    * Metric *name* must be one of the supported strings.
    * For the cosine distance, zero-norm vectors are rejected.

    If the metric name is unsupported or validation fails, *d* returns -1.

    Args:
        name (str): Name of the desired metric.

    Returns:
        Callable[[Iterable, Iterable], float | int]: A distance function with
        integrated validation.
    """

    def validate_vectors(x, y):
        if not (isinstance(x, (list, tuple, np.ndarray)) and isinstance(y, (list, tuple, np.ndarray))):
            return False
        try:
            x = np.array(x, dtype=float)
            y = np.array(y, dtype=float)
        except ValueError:
            return False
        if x.ndim != 1 or y.ndim != 1 or len(x) == 0 or (len(y) == 0) or (len(x) != len(y)):
            return False
        return (x, y)

    def euclidean_distance(x, y):
        return np.sqrt(np.sum((x - y) ** 2))

    def manhattan_distance(x, y):
        return np.sum(np.abs(x - y))

    def chebyshev_distance(x, y):
        return np.max(np.abs(x - y))

    def cosine_distance(x, y):
        norm_x = np.linalg.norm(x)
        norm_y = np.linalg.norm(y)
        if norm_x == 0 or norm_y == 0:
            return -1
        return 1 - np.dot(x, y) / (norm_x * norm_y)

    def distance_function(x, y):
        validated = validate_vectors(x, y)
        if not validated:
            return -1
        (x, y) = validated
        if name == 'euclidean':
            return round(euclidean_distance(x, y), 4)
        elif name == 'manhattan':
            return round(manhattan_distance(x, y), 4)
        elif name == 'chebyshev':
            return round(chebyshev_distance(x, y), 4)
        elif name == 'cosine':
            return round(cosine_distance(x, y), 4)
        else:
            return -1
    return distance_function

def blackjack_outcome(player: list[int], dealer: list[int]) -> float:
    """Evaluate the outcome of a finished round of Blackjack.

    The function **must** follow the rules presented in the task description.

    Args:
        player: List of integers (1–10) representing the player's final hand. 1 is Ace.
        dealer: List of integers (1–10) representing the dealer's final hand.

    Returns:
        The player's reward as a float. Possible values are -1, 0, 1 or 1.5.
    """

    def hand_value(hand: list[int]) -> int:
        """Calculate the value of a hand, considering the usable ace rule."""
        value = sum(hand)
        aces = hand.count(1)
        while value <= 11 and aces > 0:
            value += 10
            aces -= 1
        return value

    def is_natural_blackjack(hand: list[int]) -> bool:
        """Check if the hand is a natural blackjack."""
        return len(hand) == 2 and (1 in hand and 10 in hand)
    player_value = hand_value(player)
    dealer_value = hand_value(dealer)
    if player_value > 21:
        return -1
    elif dealer_value > 21:
        return 1
    if is_natural_blackjack(player) and (not is_natural_blackjack(dealer)):
        return 1.5
    if player_value > dealer_value:
        return 1
    elif player_value < dealer_value:
        return -1
    else:
        return 0

import numpy as np
def sigmoid(z):
    """Compute the element-wise sigmoid (logistic) function.

    The function must work for scalar numbers, Python lists and NumPy arrays and
    must be numerically stable for very large positive or negative inputs.

    Args:
        z: A scalar (int/float) or array-like object (list or np.ndarray)
           containing numeric values.

    Returns:
        float | list: The sigmoid of the input rounded to 4 decimal places.
        If `z` is a scalar the return value is a float, otherwise it is a Python
        list with the same shape as the input.
    """

    def _sigmoid_scalar(x):
        if x >= 0:
            return 1 / (1 + np.exp(-x))
        else:
            return np.exp(x) / (1 + np.exp(x))
    if isinstance(z, (int, float)):
        return round(_sigmoid_scalar(z), 4)
    if isinstance(z, list):
        z = np.array(z)
    result = np.vectorize(_sigmoid_scalar)(z)
    if isinstance(z, np.ndarray):
        return np.round(result, 4).tolist()
    elif isinstance(z, list):
        return [round(val, 4) for val in result.tolist()]

import numpy as np
def leaky_relu(z, a=0.01):
    """Apply the Leaky ReLU activation to every element in *z*.

    Args:
        z: A scalar, list (possibly nested) or ``numpy.ndarray`` of numbers.
        a: Optional float in [0,1) — the slope for negative inputs. Defaults to 0.01.

    Returns:
        A Python list with the same structure as *z* where each value has been transformed
        by the Leaky ReLU activation.
    """

    def apply_leaky_relu(x):
        return x if x >= 0 else a * x

    def convert_to_list(z):
        if isinstance(z, (list, np.ndarray)):
            return [convert_to_list(item) for item in z]
        return apply_leaky_relu(z)
    if isinstance(z, (int, float)):
        return apply_leaky_relu(z)
    if isinstance(z, np.ndarray):
        z = z.tolist()
    return convert_to_list(z)

