from dataclasses import dataclass
@dataclass
class Leaf:
    """A terminal node that stores a prediction value."""
    value: object
@dataclass
class Node:
    """An internal decision-tree node.

    Attributes:
        feature (int):   Index of the feature to test.
        threshold (float): Threshold that splits the data.
        left (Node | Leaf):  Sub-tree for samples with feature value < threshold.
        right (Node | Leaf): Sub-tree for samples with feature value ≥ threshold.
    """
    feature: int
    threshold: float
    left: object
    right: object
def compare_trees(tree_a, tree_b):
    """Recursively checks whether *tree_a* and *tree_b* are equivalent.

    Args:
        tree_a: Root node of the first decision tree (Node or Leaf).
        tree_b: Root node of the second decision tree (Node or Leaf).

    Returns:
        True if the two trees are equivalent, False otherwise.
    """
    if isinstance(tree_a, Leaf) and isinstance(tree_b, Leaf):
        return np.allclose(tree_a.value, tree_b.value, atol=1e-08)
    if isinstance(tree_a, Leaf) != isinstance(tree_b, Leaf):
        return False
    if isinstance(tree_a, Node) and isinstance(tree_b, Node):
        if tree_a.feature == tree_b.feature and np.isclose(tree_a.threshold, tree_b.threshold, atol=1e-08):
            return compare_trees(tree_a.left, tree_b.left) and compare_trees(tree_a.right, tree_b.right)
    return False

import numpy as np
def spectral_clustering(X: np.ndarray, k: int) -> list[int]:
    """Cluster data using Spectral Clustering.

    Args:
        X: 2-D NumPy array where each row is a data sample.
        k: Desired number of clusters.

    Returns:
        List of length n_samples containing an integer cluster label for each sample.
    """
    (n_samples, _) = X.shape

    def compute_weight(x_i, x_j):
        return 1 / (1 + np.linalg.norm(x_i - x_j))
    W = np.zeros((n_samples, n_samples))
    for i in range(n_samples):
        for j in range(n_samples):
            if i != j:
                W[i, j] = compute_weight(X[i], X[j])
    D = np.diag(np.sum(W, axis=1))
    L = D - W
    (eigenvalues, eigenvectors) = np.linalg.eigh(L)
    E = eigenvectors[:, :k]

    def k_means(E, k, max_iter=100):
        (n_samples, n_features) = E.shape
        centroids = E[:k].copy()
        labels = np.zeros(n_samples, dtype=int)
        for _ in range(max_iter):
            new_labels = np.argmin(np.linalg.norm(E[:, np.newaxis, :] - centroids, axis=2), axis=1)
            if np.array_equal(labels, new_labels):
                break
            labels = new_labels
            for i in range(k):
                if np.any(labels == i):
                    centroids[i] = np.mean(E[labels == i], axis=0)
                else:
                    centroids[i] = E[np.random.randint(n_samples)]
        return labels.tolist()
    cluster_labels = k_means(E, k)
    return cluster_labels

from typing import Any, Dict, Iterable, Tuple as PyTuple
class Space:
    pass
class Box(Space):

    def __init__(self, low: float, high: float, shape: PyTuple[int, ...]):
        self.low = low
        self.high = high
        self.shape = shape
class Tuple(Space):

    def __init__(self, spaces: Iterable[Space]):
        self.spaces = tuple(spaces)
class Dict(Space):

    def __init__(self, spaces: Dict[str, Space]):
        self.spaces = dict(spaces)
class Env:
    """Tiny environment that only stores two spaces."""

    def __init__(self, action_space: Space, observation_space: Space):
        self.action_space = action_space
        self.observation_space = observation_space
def is_continuous(env: Env, tuple_action: bool, tuple_obs: bool):
    """Determine whether the given environment's spaces are continuous.

    A space is *continuous* if it is an instance of `Box`. For composite spaces
    (`Tuple` or `Dict`) the space is continuous only if **all** its sub-spaces
    are `Box`.

    Args:
        env:          Environment exposing `action_space` and `observation_space`.
        tuple_action: Whether the *action* space is composite.
        tuple_obs:    Whether the *observation* space is composite.

    Returns:
        A tuple `(cont_action, cont_obs)` of booleans.
    """

    def _is_all_boxes(space):
        if isinstance(space, Box):
            return True
        elif isinstance(space, (Tuple, Dict)):
            if isinstance(space, Tuple):
                sub_spaces = space.spaces
            else:
                sub_spaces = space.spaces.values()
            return all((_is_all_boxes(sub_space) for sub_space in sub_spaces))
        return False
    cont_action = _is_all_boxes(env.action_space) if tuple_action else isinstance(env.action_space, Box)
    cont_obs = _is_all_boxes(env.observation_space) if tuple_obs else isinstance(env.observation_space, Box)
    return (cont_action, cont_obs)

import numpy as np
def gbdt_regression(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, n_estimators: int=10, learning_rate: float=0.1, max_depth: int=3) -> list[float]:
    """Gradient Boosting Decision Tree (GBDT) regressor.

    Parameters
    ----------
    X_train : np.ndarray
        2-D array with shape (n_samples, n_features) containing the training
        features.
    y_train : np.ndarray
        1-D array of length n_samples containing the training targets.
    X_test : np.ndarray
        2-D array with shape (m_samples, n_features) containing the test
        features to predict.
    n_estimators : int, default=10
        Number of boosting iterations.
    learning_rate : float, default=0.1
        Shrinkage factor applied to each tree’s prediction.
    max_depth : int, default=3
        Maximum depth of every individual regression tree.

    Returns
    -------
    list[float]
        Predictions for every sample in *X_test*, rounded to 4 decimal places.
    """

    class Node:

        def __init__(self, feature=None, threshold=None, left=None, right=None, value=None):
            self.feature = feature
            self.threshold = threshold
            self.left = left
            self.right = right
            self.value = value

    def mse(y_true, y_pred):
        return np.mean((y_true - y_pred) ** 2)

    def best_split(X, y):
        (m, n) = X.shape
        best_mse = float('inf')
        (best_feature, best_threshold) = (None, None)
        for feature in range(n):
            thresholds = np.unique(X[:, feature])
            for threshold in thresholds:
                left_indices = X[:, feature] <= threshold
                right_indices = X[:, feature] > threshold
                if sum(left_indices) == 0 or sum(right_indices) == 0:
                    continue
                (y_left, y_right) = (y[left_indices], y[right_indices])
                current_mse = (mse(y_left, np.mean(y_left)) * len(y_left) + mse(y_right, np.mean(y_right)) * len(y_right)) / m
                if current_mse < best_mse:
                    best_mse = current_mse
                    (best_feature, best_threshold) = (feature, threshold)
        return (best_feature, best_threshold)

    def build_tree(X, y, depth=0):
        if depth >= max_depth or mse(y, np.mean(y)) == 0:
            return Node(value=np.mean(y))
        (feature, threshold) = best_split(X, y)
        left_indices = X[:, feature] <= threshold
        right_indices = X[:, feature] > threshold
        left_subtree = build_tree(X[left_indices], y[left_indices], depth + 1)
        right_subtree = build_tree(X[right_indices], y[right_indices], depth + 1)
        return Node(feature, threshold, left_subtree, right_subtree)

    def predict_one(tree, x):
        if tree.value is not None:
            return tree.value
        if x[tree.feature] <= tree.threshold:
            return predict_one(tree.left, x)
        else:
            return predict_one(tree.right, x)

    def predict(tree, X):
        return np.array([predict_one(tree, x) for x in X])
    y_pred = np.full(X_train.shape[0], np.mean(y_train))
    trees = []
    for _ in range(n_estimators):
