def root_mean_squared_log_error(actual: list[float], predicted: list[float]) -> float:
    """Computes the Root Mean Squared Logarithmic Error (RMSLE) between two lists of values."""
    if len(actual) != len(predicted):
        return -1
    if any((x < 0 for x in actual)) or any((x < 0 for x in predicted)):
        return -1
    log_diffs = [np.log1p(p) - np.log1p(a) for (p, a) in zip(predicted, actual)]
    msle = np.mean([diff ** 2 for diff in log_diffs])
    rmsle = np.sqrt(msle)
    return round(rmsle, 4)
