def apriori_frequent_itemsets(transactions: list[list[int]], min_sup: float) -> list[tuple]:
    """Find frequent itemsets with the Apriori algorithm.

    Parameters
    ----------
    transactions : list[list[Hashable]]
        List of transactions; each transaction is itself a list of items.
    min_sup : float
        Minimum support threshold expressed as a fraction (>0 and ≤1).

    Returns
    -------
    list[tuple]
        Frequent itemsets ordered by length and then lexicographically.
    """

    def has_frequent_subset(candidate, frequent_itemsets):
        """Check if the candidate has all subsets as frequent."""
        for subset in itertools.combinations(candidate, len(candidate) - 1):
            if subset not in frequent_itemsets[len(subset)]:
                return False
        return True

    def generate_candidates(frequent_itemsets, k):
        """Generate candidate itemsets of size k from frequent itemsets of size k-1."""
        candidates = set()
        for i in range(len(frequent_itemsets[k - 1])):
            for j in range(i + 1, len(frequent_itemsets[k - 1])):
                a = frequent_itemsets[k - 1][i]
                b = frequent_itemsets[k - 1][j]
                if a[:-1] == b[:-1]:
                    candidate = tuple(sorted(a + b[-1:]))
                    if has_frequent_subset(candidate, frequent_itemsets):
                        candidates.add(candidate)
        return candidates

    def count_support(candidates, transactions):
        """Count the support of candidate itemsets in the transactions."""
        support_count = {candidate: 0 for candidate in candidates}
        for transaction in transactions:
            for candidate in candidates:
                if set(candidate).issubset(set(transaction)):
                    support_count[candidate] += 1
        return support_count

    def filter_frequent_itemsets(candidates, support_count, min_sup, num_transactions):
        """Filter out infrequent itemsets based on the support threshold."""
        frequent_itemsets = {}
        for (candidate, count) in support_count.items():
            if count / num_transactions >= min_sup:
                frequent_itemsets[candidate] = count
        return frequent_itemsets
    frequent_itemsets = {1: {}}
    num_transactions = len(transactions)
    item_counts = {}
    for transaction in transactions:
        for item in transaction:
            item_counts[item] = item_counts.get(item, 0) + 1
    for (item, count) in item_counts.items():
        if count / num_transactions >= min_sup:
            frequent_itemsets[1][item,] = count
    k = 2
    while frequent_itemsets[k - 1]:
        candidates = generate_candidates(frequent_itemsets, k)
        support_count = count_support(candidates, transactions)
        frequent_itemsets[k] = filter_frequent_itemsets(candidates, support_count, min_sup, num_transactions)
        k += 1
    result = []
    for k in range(1, len(frequent_itemsets)):
        for itemset in frequent_itemsets[k]:
            result.append(itemset)
    result.sort(key=lambda x: (len(x), x))
    return result
