        item_counts = Counter((item for transaction in transactions for item in transaction))
        frequent_items = {item: count for (item, count) in item_counts.items() if count >= min_sup}
        sorted_items = sorted(frequent_items, key=frequent_items.get, reverse=True)
        root = TreeNode(None)
        header_table = {item: [] for item in sorted_items}
        for transaction in transactions:
            filtered_transaction = [item for item in transaction if item in frequent_items]
            if filtered_transaction:
                sorted_transaction = sorted(filtered_transaction, key=lambda x: sorted_items.index(x))
                update_tree(sorted_transaction, root, header_table)
        return (root, header_table)

    def update_tree(transaction, node, header_table):
        for item in transaction:
            child = node.get_child(item)
            if child:
                child.count += 1
            else:
                new_node = TreeNode(item, parent=node)
                node.children.append(new_node)
                header_table[item].append(new_node)
            node = new_node

    class TreeNode:

        def __init__(self, item, count=1, parent=None):
            self.item = item
            self.count = count
            self.children = []
            self.parent = parent

        def get_child(self, item):
            for child in self.children:
                if child.item == item:
                    return child
            return None

        def get_prefix_path(self):
            path = []
            node = self
            while node.parent:
                path.append(node)
                node = node.parent
            path.reverse()
            return path

    def find_frequent_itemsets(header_table, prefix, min_sup, frequent_itemsets):
        for base_item in sorted(header_table, key=lambda x: len(header_table[x])):
            new_frequent_set = prefix + [base_item]
            if len(new_frequent_set) > 1:
                frequent_itemsets.append(tuple(new_frequent_set))
            conditional_patterns = []
            for node in header_table[base_item]:
                path = node.get_prefix_path()
                if len(path) > 1:
                    conditional_patterns.extend([tuple((p.item for p in path[:-1]))] * node.count)
            if conditional_patterns:
                (conditional_tree, conditional_header_table) = construct_fptree(conditional_patterns, min_sup)
                find_frequent_itemsets(conditional_header_table, new_frequent_set, min_sup, frequent_itemsets)
