import math
def detect_outliers(data: np.ndarray, n_trees: int, sample_size: int, epsilon: float) -> list[int]:
    """Tiny Isolation Forest implementation.

    Args:
        data: 2-D NumPy array where each row is an observation and each column a feature.
        n_trees: Number of random isolation trees that will be grown.
        sample_size: Number of samples drawn (without replacement) to grow every tree.
        epsilon: Fraction (0 ≤ ε < 1) of observations that are considered outliers.

    Returns:
        A list with the zero-based indices of the detected outliers, sorted increasingly.
    """
    np.random.seed(42)

    def c(n):
        if n == 1:
            return 0
        else:
            return 2 * np.log(n - 1) + 0.5772156649 - 2 * (n - 1) / n

    def height_limit(sample_size):
        return math.ceil(math.log2(sample_size))

    class IsolationTree:

        def __init__(self, data, height=0, height_limit=math.inf):
            self.height = height
            self.height_limit = height_limit
            self.left = None
            self.right = None
            self.split_feature = None
            self.split_value = None
            if data.shape[0] <= 1 or height >= height_limit:
                self.size = data.shape[0]
            else:
                self.split_feature = np.random.choice(data.shape[1])
                self.split_value = np.random.uniform(data[:, self.split_feature].min(), data[:, self.split_feature].max())
                left_data = data[data[:, self.split_feature] < self.split_value]
                right_data = data[data[:, self.split_feature] >= self.split_value]
                self.left = IsolationTree(left_data, height + 1, height_limit)
                self.right = IsolationTree(right_data, height + 1, height_limit)

        def path_length(self, x, current_length=0):
            if self.split_feature is None or self.height >= self.height_limit:
                return current_length + c(self.size)
            elif x[self.split_feature] < self.split_value:
                return self.left.path_length(x, current_length + 1)
            else:
                return self.right.path_length(x, current_length + 1)

    def build_forest(data, n_trees, sample_size):
        forest = []
        for _ in range(n_trees):
            sample_indices = np.random.choice(data.shape[0], size=min(sample_size, data.shape[0]), replace=False)
            sample_data = data[sample_indices]
            tree = IsolationTree(sample_data, height_limit=height_limit(sample_size))
            forest.append(tree)
        return forest

    def compute_anomaly_scores(forest, data):
        path_lengths = np.zeros(data.shape[0])
        for i in range(data.shape[0]):
            path_lengths[i] = np.mean([tree.path_length(data[i]) for tree in forest])
        phi = 2 * np.log(data.shape[0] - 1) - 2 * (data.shape[0] - 1) / data.shape[0]
        scores = 2 ** (-path_lengths / phi)
        return scores
    forest = build_forest(data, n_trees, sample_size)
    scores = compute_anomaly_scores(forest, data)
    threshold = np.quantile(scores, 1 - epsilon)
    outlier_indices = np.where(scores >= threshold)[0]
    return sorted(outlier_indices)

