def local_outlier_factor(data, k, epsilon=1.5):
    n = data.shape[0]
    distance_matrix = np.zeros((n, n))
    for i in range(n):
        for j in range(i, n):
            dist = euclidean_distance(data[i], data[j])
            distance_matrix[i][j] = dist
            distance_matrix[j][i] = dist
    k_distances = np.zeros(n)
    k_nearest_neighbours = [None] * n
    for i in range(n):
        sorted_indices = np.argsort(distance_matrix[i])
        k_distances[i] = distance_matrix[i, sorted_indices[k]]
        k_nearest_neighbours[i] = sorted_indices[1:k + 1]

    def reach_dist(p, q):
        return max(k_distances[q], distance_matrix[p, q])
    lrd = np.zeros(n)
    for i in range(n):
        sum_reach_dists = sum((reach_dist(i, j) for j in k_nearest_neighbours[i]))
        lrd[i] = k / sum_reach_dists
    lof_scores = np.zeros(n)
    for i in range(n):
        sum_lrd = sum((lrd[j] for j in k_nearest_neighbours[i]))
        lof_scores[i] = sum_lrd / k / lrd[i]
    lof_scores = np.round(lof_scores, 4)
    outlier_indices = [i for i in range(n) if lof_scores[i] > epsilon]
    return (lof_scores.tolist(), outlier_indices)
