def escape_fire_maze(grid: list[str]) -> int:
    """Escape from a maze with spreading fire."""
    n = len(grid)
    if n == 1:
        if grid[0][0] == '.':
            return 0
        else:
            return -1
    directions = [(0, 1), (1, 0), (0, -1), (-1, 0)]
    fire_queue = deque()
    agent_queue = deque()
    for i in range(n):
        for j in range(n):
            if grid[i][j] == 'F':
                fire_queue.append((i, j, 0))
    if grid[0][0] == 'F' or grid[n - 1][n - 1] == 'F':
        return -1
    agent_queue.append((0, 0, 0))
    while fire_queue or agent_queue:
        if fire_queue:
            (x, y, t) = fire_queue.popleft()
            for (dx, dy) in directions:
                (nx, ny) = (x + dx, y + dy)
                if 0 <= nx < n and 0 <= ny < n and (grid[nx][ny] != '#'):
                    grid[nx][ny] = 'F'
                    fire_queue.append((nx, ny, t + 1))
        if agent_queue:
            (x, y, t) = agent_queue.popleft()
            if (x, y) == (n - 1, n - 1):
                return t
            for (dx, dy) in directions:
                (nx, ny) = (x + dx, y + dy)
                if 0 <= nx < n and 0 <= ny < n and (grid[nx][ny] == '.') and (grid[nx][ny] != 'F'):
                    grid[nx][ny] = '.'
                    agent_queue.append((nx, ny, t + 1))
    return -1
