def knn_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, k: int, metric: str='euclidean') -> np.ndarray:
    """Implement your code here."""

    def euclidean_distance(x1, x2):
        return distance.euclidean(x1, x2)

    def manhattan_distance(x1, x2):
        return distance.cityblock(x1, x2)

    def cosine_distance(x1, x2):
        return distance.cosine(x1, x2)
    distance_funcs = {'euclidean': euclidean_distance, 'manhattan': manhattan_distance, 'cosine': cosine_distance}
    distance_func = distance_funcs.get(metric, euclidean_distance)
    predictions = []
    for test_sample in X_test:
        distances = [distance_func(test_sample, train_sample) for train_sample in X_train]
        k_indices = np.argsort(distances)[:k]
        k_nearest_labels = y_train[k_indices]
        predicted_label = np.argmax(np.bincount(k_nearest_labels))
        predictions.append(predicted_label)
    return np.array(predictions)
