import numpy as np
import numpy as np
def gini(labels: np.ndarray) -> float:
    """Calculate Gini impurity of a set of labels."""
    if len(labels) == 0:
        return 0
    (_, counts) = np.unique(labels, return_counts=True)
    probabilities = counts / len(labels)
    return 1 - np.sum(np.square(probabilities))
def best_split(X: np.ndarray, y: np.ndarray) -> tuple[float, int, str]:
    """Find the best split point for a single feature."""
    unique_features = np.unique(X, axis=0)
    (best_gini_gain, best_feature, best_threshold) = (0.0, None, None)
    original_gini = gini(y)
    for feature_values in unique_features:
        for i in range(len(feature_values)):
            left_mask = X[:, i] <= feature_values[i]
            right_mask = X[:, i] > feature_values[i]
            if not np.any(left_mask) or not np.any(right_mask):
                continue
            (left_labels, right_labels) = (y[left_mask], y[right_mask])
            (gini_left, gini_right) = (gini(left_labels), gini(right_labels))
            weighted_gini = len(left_labels) / len(y) * gini_left + len(right_labels) / len(y) * gini_right
            gini_gain = original_gini - weighted_gini
            if gini_gain > best_gini_gain:
                (best_gini_gain, best_feature, best_threshold) = (gini_gain, i, feature_values[i])
    return (best_gini_gain, best_feature, best_threshold)
def fit_tree(X: np.ndarray, y: np.ndarray, current_depth: int, max_depth: int | None) -> dict:
    """Recursively fit the decision tree."""
    if len(np.unique(y)) == 1 or (max_depth is not None and current_depth >= max_depth):
        return {'label': np.argmax(np.bincount(y))}
    (best_gain, bestFeature, bestThreshold) = best_split(X, y)
    if best_gain == 0:
        return {'label': np.argmax(np.bincount(y))}
    left_mask = X[:, bestFeature] <= bestThreshold
    right_mask = X[:, bestFeature] > bestThreshold
    if not np.any(left_mask) or not np.any(right_mask):
        return {'label': np.argmax(np.bincount(y))}
    left_subtree = fit_tree(X[left_mask], y[left_mask], current_depth + 1, max_depth)
    right_subtree = fit_tree(X[right_mask], y[right_mask], current_depth + 1, max_depth)
    return {'feature': bestFeature, 'threshold': bestThreshold, 'left': left_subtree, 'right': right_subtree}
def predict_node(node: dict, x: np.ndarray) -> int:
    """Predict the class for a single sample using the decision tree node."""
    if 'label' in node:
        return node['label']
    (feature, threshold, left, right) = (node['feature'], node['threshold'], node['left'], node['right'])
    if x[feature] <= threshold:
        return predict_node(left, x)
    else:
        return predict_node(right, x)
def decision_tree_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, max_depth: int | None=None) -> list[int]:
    """Build a CART decision tree on (X_train, y_train) and predict labels for X_test."""
    tree = fit_tree(X_train, y_train, 0, max_depth)
    return [predict_node(tree, x) for x in X_test]
