import numpy as np
def rbf_kernel(X: list[list[int | float]], Y: list[list[int | float]] | None=None, sigma: float | list[float] | None=None) -> list[list[float]]:
    """Compute the Radial Basis Function (RBF) kernel matrix.

    The function should follow the specifications given in the task
    description. It must return -1 on invalid input, otherwise a nested list
    containing the kernel matrix rounded to four decimal places.
    """

    def check_inputs(X, Y, sigma):
        if not isinstance(X, list) or not all((isinstance(vec, list) for vec in X)):
            return False
        if Y is not None and (not isinstance(Y, list) or not all((isinstance(vec, list) for vec in Y))):
            return False
        if Y is not None and len(X[0]) != len(Y[0]):
            return False
        if sigma is not None:
            if isinstance(sigma, (int, float)):
                if sigma <= 0:
                    return False
            elif isinstance(sigma, list):
                if not all((isinstance(s, (int, float)) for s in sigma)):
                    return False
                if len(sigma) != len(X[0]):
                    return False
                if any((s <= 0 for s in sigma)):
                    return False
            else:
                return False
        return True

    def compute_kernel(x, y, sigma):
        diff = np.array(x) - np.array(y)
        if isinstance(sigma, (int, float)):
            return np.exp(-0.5 * np.sum(diff ** 2) / sigma ** 2)
        else:
            return np.exp(-0.5 * np.sum((diff / sigma) ** 2))
    if not check_inputs(X, Y, sigma):
        return -1
    (n, c) = (len(X), len(X[0]))
    m = len(Y) if Y else n
    if sigma is None:
        sigma = np.sqrt(c / 2)
    kernel_matrix = [[round(compute_kernel(X[i], Y[j], sigma), 4) if Y else round(compute_kernel(X[i], X[j], sigma), 4) for j in range(m)] for i in range(n)]
    return kernel_matrix
