def maxent_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, epsilon: float=0.001, n_iter: int=100) -> list[int]:
    """Implements a Maximum Entropy classifier using Generalized Iterative Scaling."""

    def build_empirical_distributions(X, y):
        num_features = X.shape[1]
        target_count = defaultdict(int)
        feature_label_count = defaultdict(int)
        feature_only_count = defaultdict(lambda : defaultdict(int))
        for (x, t) in zip(X, y):
            target_count[t] += 1
            for (i, (feat_val, label)) in enumerate(zip(x, t)):
                feature_label_count[i, label] += 1
                feature_only_count[i][feat_val] += 1
        return (target_count, feature_label_count, feature_only_count, num_features)

    def compute_gradients(X, y, w, Z_x):
        gradients = defaultdict(float)
        num_features = X.shape[1]
        for (x, t) in zip(X, y):
            Z_t = math.exp(np.dot(w, feature_label_count[t].values()))
            for i in range(num_features):
                feat_val = x[i]
                if feat_val in feature_only_count[i]:
                    gradients[i, feat_val, t] = feature_only_count[i][feat_val] / Z_t - Z_x[i, t] / Z_t ** 2
        return gradients

    def update_weights(w, gradients, Z_x):
        max_update = 0
        for ((i, feat_val, t), grad) in gradients.items():
            update = grad * Z_x[i, t]
            w[i, feat_val, t] += update
            max_update = max(max_update, abs(update))
        return (w, max_update)

    def normalize_weights(w, Z_x):
        for i in range(num_features):
            for feat_val in feature_only_count[i]:
                for t in target_count:
                    w[i, feat_val, t] /= Z_x[i, t]
        return w
    (target_count, feature_label_count, feature_only_count, num_features) = build_empirical_distributions(X_train, y_train)
    w = np.zeros((num_features, max(feature_only_count[i]) + 1, len(target_count)))
    Z_x = np.zeros((num_features, len(target_count)))
    for i in range(num_features):
        for feat_val in feature_only_count[i]:
            for t in target_count:
                if (i, feat_val, t) in w:
                    w[i, feat_val, t] = math.log(feature_only_count[i][feat_val] / target_count[t])
                    Z_x[i, t] = math.exp(np.dot(w[i, feat_val, t], feature_label_count[t].values()))
    for iteration in range(n_iter):
        gradients = compute_gradients(X_train, y_train, w, Z_x)
