def find_frequent_itemsets(transactions: List[List[Hashable]], minsup: float) -> Dict[Tuple[Hashable, ...], int]:
    """Return all frequent itemsets in **transactions**.

    Args:
        transactions: List where each element represents a transaction and is
                       itself a list of items (hashable Python objects).
        minsup:       Minimum support given as a fraction between 0 and 1.

    Returns:
        A dictionary that maps every frequent itemset (stored as a tuple of
        sorted items) to its absolute support count.  If no itemset reaches
        the threshold the function must return an empty dictionary.
    """
    support_threshold = math.ceil(minsup * len(transactions))
    item_count = Counter()
    for transaction in transactions:
        item_count.update(Counter(Counter(transaction).keys()))
    frequent_items = {item: count for (item, count) in item_count.items() if count >= support_threshold}
    frequent_itemsets = {frozenset([item]): count for (item, count) in frequent_items.items()}
    k = 2
    while True:
        candidate_itemsets = {frozenset(cand) for cand in combinations(frequent_itemsets.keys(), k)}
        if not candidate_itemsets:
            break
        candidate_counts = Counter()
        for transaction in transactions:
            transaction_items = set(Counter(transaction).keys())
            for itemset in candidate_itemsets:
                if transaction_items.issuperset(itemset):
                    candidate_counts[itemset] += 1
        frequent_itemsets = {itemset: count for (itemset, count) in candidate_counts.items() if count >= support_threshold}
        if not frequent_itemsets:
            break
        k += 1
    frequent_itemsets = {tuple(sorted(itemset)): count for (itemset, count) in frequent_itemsets.items()}
    return frequent_itemsets

import numpy as np
def sigmoid_activation(x):
    """Compute the sigmoid of *x* and its gradient.

    Parameters
    ----------
    x : float | int | list | numpy.ndarray
        Input data that can be a scalar, a Python list, or a NumPy array.

    Returns
    -------
    tuple
        A tuple (sigmoid_x, gradient_x)
        where each element is rounded to 4 decimal places and returned as:
        • float when *x* is scalar
        • Python list when *x* is array-like
    """

    def sigmoid(n):
        return 1 / (1 + np.exp(-n))

    def sigmoid_gradient(n):
        s = sigmoid(n)
        return s * (1 - s)
    if isinstance(x, np.ndarray):
        sigmoid_x = np.round(sigmoid(x), 4)
        gradient_x = np.round(sigmoid_gradient(x), 4).tolist()
    elif isinstance(x, list):
        sigmoid_x = [round(sigmoid(val), 4) for val in x]
        gradient_x = [round(sigmoid_gradient(val), 4) for val in x]
    else:
        sigmoid_x = round(sigmoid(x), 4)
        gradient_x = round(sigmoid_gradient(x), 4)
    return (sigmoid_x, gradient_x)

