def forward_algorithm(S: list[float], A: list[list[float]], B: list[list[float]], observations: list[int]) -> float:
    """Forward algorithm for Hidden Markov Models.

    Args:
        S (list[float]): Initial state probabilities.
        A (list[list[float]]): State–transition probabilities.
        B (list[list[float]]): Emission probabilities.
        observations (list[int]): Observation index sequence.

    Returns:
        float: Sequence likelihood rounded to 4 decimals, or −1 on invalid input.
    """
    n = len(S)
    m = len(B[0])
    if not (isinstance(S, list) and isinstance(A, list) and isinstance(B, list) and isinstance(observations, list)):
        return -1
    if any((not isinstance(x, list) for x in A)) or any((len(x) != n for x in A)):
        return -1
    if any((not isinstance(x, list) for x in B)) or any((len(x) != m for x in B)):
        return -1
    if len(S) != n or len(A) != n or any((len(row) != n for row in A)):
        return -1
    if len(B) != n:
        return -1
    if not all((len(row) == m for row in B)):
        return -1
    if any((sum(probabilities) != 1 for probabilities in (S, *A, *B))):
        return -1
    if any((observation < 0 or observation >= m for observation in observations)):
        return -1
    if not observations:
        return -1
    T = len(observations)
    alpha = np.zeros((n, T))
    for i in range(n):
        alpha[i, 0] = S[i] * B[i][observations[0]]
    for t in range(1, T):
        for j in range(n):
            transition_probabilities = [alpha[i, t - 1] * A[i][j] for i in range(n)]
            emission_probability = B[j][observations[t]]
            alpha[j, t] = sum(transition_probabilities) * emission_probability
    final_probability = sum((alpha[i, T - 1] for i in range(n)))
    return round(final_probability, 4)
