TIME_STEPS = 20
def string_to_int(text: str, time_steps: int, vocabulary: dict[str, int]) -> list[int]:
    """Converts a raw text string into a fixed-length list of integer token IDs."""
    char_ids = [vocabulary.get(char, 0) for char in text]
    if len(char_ids) > time_steps:
        char_ids = char_ids[:time_steps]
    else:
        char_ids += [0] * (time_steps - len(char_ids))
    return char_ids
def int_to_string(indices, inverse_vocab: dict[int, str]) -> str:
    """Converts a list of integer ids back to a text string, ignoring padding tokens."""
    return ''.join([inverse_vocab[id] for id in indices if id != 0])
def run_example(model, input_vocabulary: dict[str, int], inv_output_vocabulary: dict[int, str], text: str) -> str:
    """Encodes text to integers, predicts with the model, and decodes back to a string."""
    encoded = np.array(string_to_int(text, TIME_STEPS, input_vocabulary))
    prediction = model.predict(encoded.reshape(1, TIME_STEPS))
    predicted_ids = np.argmax(prediction, axis=-1).flatten()
    return int_to_string(predicted_ids, inv_output_vocabulary)

