    return np.array(predictions)

import numpy as np
def generate_window(window: str, N: int, coefficients: list[float] | None=None) -> list[float]:
    """Generate coefficients for several common symmetric window functions."""
    if not isinstance(N, int) or N <= 0:
        raise ValueError('N must be a positive integer.')
    if window == 'hamming':
        return [round(0.54 - 0.46 * np.cos(2 * np.pi * n / (N - 1)), 4) for n in range(N)]
    elif window == 'hann':
        return [round(0.5 - 0.5 * np.cos(2 * np.pi * n / (N - 1)), 4) for n in range(N)]
    elif window == 'blackman_harris':
        (a0, a1, a2, a3) = (0.35875, 0.48829, 0.14128, 0.01168)
        return [round(a0 - a1 * np.cos(2 * np.pi * n / (N - 1)) + a2 * np.cos(4 * np.pi * n / (N - 1)) - a3 * np.cos(6 * np.pi * n / (N - 1)), 4) for n in range(N)]
    elif window == 'generalized_cosine':
        if coefficients is None or len(coefficients) == 0:
            raise ValueError('coefficients list must not be empty for generalized_cosine.')
        if len(coefficients) > N:
            raise ValueError('Number of coefficients cannot exceed N for generalized_cosine.')
        result = []
        for n in range(N):
            term_sum = sum((coefficients[k] * np.cos(2 * np.pi * k * n / (N - 1)) for k in range(len(coefficients))))
            result.append(round(term_sum, 4))
        return result
    else:
        raise ValueError('Unknown window type.')

import numpy as np
def gini(labels: np.ndarray) -> float:
    """Calculate Gini impurity of a set of labels."""
    if len(labels) == 0:
        return 0
    (_, counts) = np.unique(labels, return_counts=True)
    probabilities = counts / len(labels)
    return 1 - np.sum(np.square(probabilities))
def best_split(X: np.ndarray, y: np.ndarray) -> tuple[float, int, str]:
    """Find the best split point for a single feature."""
    unique_features = np.unique(X, axis=0)
    (best_gini_gain, best_feature, best_threshold) = (0.0, None, None)
    original_gini = gini(y)
    for feature_values in unique_features:
        for i in range(len(feature_values)):
            left_mask = X[:, i] <= feature_values[i]
            right_mask = X[:, i] > feature_values[i]
            if not np.any(left_mask) or not np.any(right_mask):
                continue
            (left_labels, right_labels) = (y[left_mask], y[right_mask])
            (gini_left, gini_right) = (gini(left_labels), gini(right_labels))
            weighted_gini = len(left_labels) / len(y) * gini_left + len(right_labels) / len(y) * gini_right
            gini_gain = original_gini - weighted_gini
            if gini_gain > best_gini_gain:
                (best_gini_gain, best_feature, best_threshold) = (gini_gain, i, feature_values[i])
    return (best_gini_gain, best_feature, best_threshold)
def fit_tree(X: np.ndarray, y: np.ndarray, current_depth: int, max_depth: int | None) -> dict:
    """Recursively fit the decision tree."""
    if len(np.unique(y)) == 1 or (max_depth is not None and current_depth >= max_depth):
        return {'label': np.argmax(np.bincount(y))}
    (best_gain, bestFeature, bestThreshold) = best_split(X, y)
    if best_gain == 0:
        return {'label': np.argmax(np.bincount(y))}
    left_mask = X[:, bestFeature] <= bestThreshold
    right_mask = X[:, bestFeature] > bestThreshold
    if not np.any(left_mask) or not np.any(right_mask):
        return {'label': np.argmax(np.bincount(y))}
    left_subtree = fit_tree(X[left_mask], y[left_mask], current_depth + 1, max_depth)
    right_subtree = fit_tree(X[right_mask], y[right_mask], current_depth + 1, max_depth)
    return {'feature': bestFeature, 'threshold': bestThreshold, 'left': left_subtree, 'right': right_subtree}
def predict_node(node: dict, x: np.ndarray) -> int:
    """Predict the class for a single sample using the decision tree node."""
    if 'label' in node:
        return node['label']
    (feature, threshold, left, right) = (node['feature'], node['threshold'], node['left'], node['right'])
    if x[feature] <= threshold:
        return predict_node(left, x)
    else:
        return predict_node(right, x)
def decision_tree_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, max_depth: int | None=None) -> list[int]:
    """Build a CART decision tree on (X_train, y_train) and predict labels for X_test."""
    tree = fit_tree(X_train, y_train, 0, max_depth)
    return [predict_node(tree, x) for x in X_test]

import numpy as np
def metric(name: str):
    """Factory producing a validated distance function.

    The function creates and returns a callable *d* that computes one of four
    classical distances (Euclidean, Manhattan, Chebyshev, Cosine) between two
    numeric vectors.  All numeric outputs are rounded to four decimal places.

    Validation rules inside the returned callable:
    * Both arguments must be one-dimensional, non-empty, equal-length numeric
      iterables (list, tuple or NumPy array).
    * Metric *name* must be one of the supported strings.
    * For the cosine distance, zero-norm vectors are rejected.

    If the metric name is unsupported or validation fails, *d* returns -1.

    Args:
        name (str): Name of the desired metric.

    Returns:
        Callable[[Iterable, Iterable], float | int]: A distance function with
        integrated validation.
    """

    def validate_and_convert(x, y):
        if not isinstance(x, (list, tuple, np.ndarray)) or not isinstance(y, (list, tuple, np.ndarray)):
            return (False, 'Both inputs must be list, tuple, or NumPy array.')
        (x, y) = (np.array(x, dtype=float), np.array(y, dtype=float))
        if x.ndim != 1 or y.ndim != 1 or len(x) != len(y) or (len(x) == 0):
            return (False, 'Inputs must be one-dimensional and of equal non-zero length.')
        if np.linalg.norm(x) == 0 and np.linalg.norm(y) == 0:
            return (False, 'Vectors with zero norm are not allowed for cosine distance.')
        return (True, x, y)
    if name not in ['euclidean', 'manhattan', 'chebyshev', 'cosine']:
        return lambda *args: -1

    def calculate_distance(x, y):
        if name == 'euclidean':
            return np.round(np.sqrt(np.sum((x - y) ** 2)), 4)
        elif name == 'manhattan':
            return np.round(np.sum(np.abs(x - y)), 4)
        elif name == 'chebyshev':
            return np.round(np.max(np.abs(x - y)), 4)
        elif name == 'cosine':
            dot_product = np.dot(x, y)
            norm_x = np.linalg.norm(x)
            norm_y = np.linalg.norm(y)
            if norm_x == 0 or norm_y == 0:
                return -1
            return np.round(1 - dot_product / (norm_x * norm_y), 4)
    return lambda x, y: calculate_distance(*validate_and_convert(x, y)[1])

